    def refresh_addresses(self):
        import socket
        self.addresses = []
        for interface in socket.if_nameindex():
            try:
                addrs = socket.ifaddr(interface[0])
                for addr in addrs:
                    if addr[0] == 'IPv4':
                        self.addresses.append(addr[1])
