            else:
                sock.bind(('', self.MSEARCH_PORT))
            sock.setsockopt(socket.IPPROTO_IP, socket.IP_MULTICAST_TTL, self.SSDP_TTL)
            for _ in range(self.SSDP_AMOUNT):
                self._send_discover(sock, self.SSDP_MX)
            while True:
                try:
                    data, addr = sock.recvfrom(self.BUFFER_SIZE)
                    self.cb_on_device_response(data, addr)
