        }
        await self._ws.send(json.dumps(auth_msg))

    async def _start_ws(self):
        await self._connect()
        await self._auth()

    async def close(self):
        if self._ws:
            await self._ws.close()
            self._running = False

    def stop_ws(self):
        self._stop_stream_queue.put(True)

    async def _consume(self):
        while self._should_run and self._running:
            try:
                message = await self._ws.recv()
                msg = json.loads(message)
                self._dispatch(msg)
            except websockets.exceptions.ConnectionClosed:
                self._running = False
                break

    def _dispatch(self, msg: Dict):
        if "type" in msg:
            handler_type = msg["type"]
            if handler_type in self._handlers:
                for symbol, handler in self._handlers[handler_type].items():
                    if "symbol" in msg and msg["symbol"] == symbol:
                        handler(msg)

    def _subscribe(self, handler: Callable, symbols: List[str], handlers: Dict):
        for symbol in symbols:
            handlers[symbol] = handler

    def _subscribe_all(self):
        for data_type, handlers in self._handlers.items():
            for symbol, handler in handlers.items():
                subscribe_msg = {
                    "action": "subscribe",
                    "type": data_type,
                    "symbol": symbol
                }
                asyncio.run_coroutine_threadsafe(self._ws.send(json.dumps(subscribe_msg)), self._loop)

    async def _run_forever(self):
        await self._start_ws()
        self._subscribe_all()
        await self._consume()

    def subscribe_trades(self, handler: Callable, *symbols: str):
        self._subscribe(handler, list(symbols), self._handlers["trades"])

    def subscribe_quotes(self, handler: Callable, *symbols: str):
        self._subscribe(handler, list(symbols), self._handlers["quotes"])

    def subscribe_bars(self, handler: Callable, *symbols: str):
        self._subscribe(handler, list(symbols), self._handlers["bars"])

    def subscribe_updated_bars(self, handler: Callable, *symbols: str):
        self._subscribe(handler, list(symbols), self._handlers["updated_bars"])

    def subscribe_daily_bars(self, handler: Callable, *symbols: str):
        self._subscribe(handler, list(symbols), self._handlers["daily_bars"])

    def stop(self):
        self._should_run = False
        self.stop_ws()
        if self._ws:
            self._loop.create_task(self.close())
