import numpy as np
from typing import Optional, List, Union, Generator, Tuple

class BertClient:
    def __init__(self, ip='localhost', port=5555, port_out=5556, identity=None, timeout=-1):
        self.context = zmq.Context()
        self.sender = self.context.socket(zmq.PUSH)
        self.receiver = self.context.socket(zmq.SUB)
        self.identity = identity if identity else b'client'
        self.request_id = 0
        self.timeout = timeout
        self.pending_requests = set()
        self.sender.connect(f'tcp://{ip}:{port}')
        self.receiver.connect(f'tcp://{ip}:{port_out}')
        self.receiver.setsockopt(zmq.SUBSCRIBE, b'')

    def close(self):
        self.sender.close()
        self.receiver.close()
        self.context.term()

    def _send(self, msg, msg_len=0):
        self.sender.send(msg)

    def _recv(self):
        try:
            return self.receiver.recv(zmq.NOBLOCK)
        except zmq.Again:
            return None

    def _recv_ndarray(self):
        data = self._recv()
        if data is None:
            return None
        return np.frombuffer(data, dtype=np.float32)

    def encode(self, texts, blocking=True):
        self.request_id += 1
        self.pending_requests.add(self.request_id)
        self._send(f'{self.request_id} {len(texts)}'.encode())
        self._send(' '.join(texts).encode())
        if blocking:
            while self.request_id in self.pending_requests:
                pass
            return self._recv_ndarray()
        return None

    def fetch(self, delay=0.0):
        while self.pending_requests:
            response = self._recv_ndarray()
            if response is not None:
                yield response
            time.sleep(delay)

    def fetch_all(self, sort=True, concat=False):
        responses = []
        for response in self.fetch():
            responses.append(response)
        if sort:
            responses.sort()
        if concat:
            return np.concatenate(responses)
        return responses

import zmq
import threading
import numpy as np
from collections import deque
import time
import json
import zlib

class NetGear:
    def __init__(self, address=None, port=None, protocol=None, pattern=0, receive_mode=False, logging=False, **options):
        self.__logging = logging
        self.__msg_context = zmq.Context()
        self.__receive_mode = receive_mode
        self.__pattern = pattern
        self.__terminate = False
        self.__msg_socket = None
        self.__subscriber_timeout = 1000
        self.__queue = deque()
        self.__thread = None
        self.__compression = options.get("compression", False)
        self.__address = address
        self.__port = port
        self.__protocol = protocol or "tcp"
        self.__connect()

    def __connect(self):
        if self.__pattern == 2:  # PUB/SUB
            if self.__receive_mode:
                self.__msg_socket = self.__msg_context.socket(zmq.SUB)
                self.__msg_socket.setsockopt(zmq.SUBSCRIBE, b"")
                self.__msg_socket.setsockopt(zmq.RCVTIMEO, self.__subscriber_timeout)
            else:
                self.__msg_socket = self.__msg_context.socket(zmq.PUB)
        else:
            raise ValueError("Unsupported pattern")

        if not self.__receive_mode:
            self.__msg_socket.connect(f"{self.__protocol}://{self.__address}:{self.__port}")
        else:
            self.__msg_socket.bind(f"{self.__protocol}://{self.__address}:{self.__port}")
            self.__thread = threading.Thread(target=self.__recv_handler)
            self.__thread.start()

    def __recv_handler(self):
        while not self.__terminate:
            try:
                frame = self.__msg_socket.recv()
                if self.__compression:
                    frame = zlib.decompress(frame)
                self.__queue.append(frame)
            except zmq.Again:
                pass
            except Exception as e:
                if self.__logging:
                    print(f"Error receiving frame: {e}")
                break

    def recv(self):
        if not self.__receive_mode:
            return None
        if self.__queue:
            return np.frombuffer(self.__queue.popleft(), dtype=np.uint8)
        return None

    def send(self, frame, message=None):
        if self.__receive_mode:
            return
        if message is None:
            message = {}
        message["frame"] = frame.tobytes()
        message["shape"] = frame.shape
        message["dtype"] = str(frame.dtype)
        serialized = json.dumps(message).encode("utf-8")
        if self.__compression:
            serialized = zlib.compress(serialized)
        self.__msg_socket.send(serialized)

    def close(self, kill=False):
        self.__terminate = True
        if self.__thread and self.__thread.is_alive():
            self.__thread.join()
        if self.__msg_socket:
            self.__msg_socket.close()
        self.__msg_context.term()

