import threading
import grpc
from concurrent import futures
from typing import Dict, Iterator, List, Optional, Tuple, Any
from google.protobuf import empty_pb2

class ModelResponse:
    def __init__(self, async_pipeline: "AsyncPipeline" = None):
        self._stop_event = threading.Event()
        self.inference_pipeline = async_pipeline
        self.method_name_to_task = {}
        self.lock = threading.Lock()

    def Terminate(self, request: empty_pb2.Empty, context: grpc.ServicerContext) -> empty_pb2.Empty:
        self._stop_event.set()
        return empty_pb2.Empty()

    def get_stop_event(self) -> threading.Event:
        return self._stop_event

    def GeneratorReply(self, request: "ModelRequest", context: grpc.ServicerContext) -> "ModelResponse":
        with self.lock:
            if self._stop_event.is_set():
                context.abort(grpc.StatusCode.SHUTDOWN, "Server is terminating")
            task_methods = self._get_task_methods(context.invocation_metadata()[0][1])
            result = self.inference_pipeline.run(request, task_methods)
            return result

    def GeneratorReplyStream(self, request: "ModelRequest", context: grpc.ServicerContext) -> Iterator["ModelResponse"]:
        with self.lock:
            if self._stop_event.is_set():
                context.abort(grpc.StatusCode.SHUTDOWN, "Server is terminating")
            task_methods = self._get_task_methods(context.invocation_metadata()[0][1])
            for result in self.inference_pipeline.run_stream(request, task_methods):
                yield result

    def _get_task_methods(self, method_name: str) -> Dict[str, "TaskMethods"]:
        return self.method_name_to_task.get(method_name, {})

def _do_serve(service_impl: ModelResponse, port: int, interceptors: Optional[List[grpc.ServerInterceptor]] = None):
    server = grpc.server(futures.ThreadPoolExecutor(max_workers=10), interceptors=interceptors)
    service_impl.register(server)
    server.add_insecure_port(f"[::]:{port}")
    server.start()
    service_impl.get_stop_event().wait()
    server.stop(0)
