import queue
from typing import Any, Optional, Mapping, Union, Callable, Type
from http.client import HTTPConnection, HTTPSConnection, BaseHTTPResponse
from urllib.parse import urlparse
from .timeout import Timeout
from .retry import Retry
from .url import Url

class HTTPConnectionPool:
    def __init__(self, host: str, port: Optional[int] = None, timeout: Union[Timeout, float, None] = None, maxsize: int = 1, block: bool = False, headers: Optional[Mapping[str, str]] = None, retries: Union[Retry, bool, int, None] = None, _proxy: Optional[Url] = None, _proxy_headers: Optional[Mapping[str, str]] = None, **conn_kw: Any) -> None:
        self.scheme = "https" if isinstance(HTTPSConnection, type) else "http"
        self.ConnectionCls = HTTPSConnection if self.scheme == "https" else HTTPConnection
        self.QueueCls = queue.LifoQueue
        self.host = host
        self.port = port
        self.timeout = timeout
        self.retries = retries
        self.pool = self.QueueCls(maxsize)
        self.block = block
        self.proxy = _proxy
        self.proxy_headers = _proxy_headers
        self.headers = headers
        self.conn_kw = conn_kw

    def _new_conn(self) -> HTTPConnection:
        if self.proxy:
            proxy_url = self.proxy
            proxy_host = proxy_url.host
            proxy_port = proxy_url.port or (443 if proxy_url.scheme == "https" else 80)
            conn = self.ConnectionCls(proxy_host, proxy_port, **self.conn_kw)
            if self.proxy_headers:
                for header, value in self.proxy_headers.items():
                    conn.putheader(header, value)
            conn.endheaders()
        else:
            conn = self.ConnectionCls(self.host, self.port, **self.conn_kw)
        return conn

    def _get_conn(self, timeout: Optional[float] = None) -> HTTPConnection:
        try:
            conn = self.pool.get(timeout=timeout if self.block else 0)
        except queue.Empty:
            conn = self._new_conn()
        return conn

    def _put_conn(self, conn: Optional[HTTPConnection]) -> None:
        if conn:
            try:
                self.pool.put(conn, block=self.block)
            except queue.Full:
                pass

    def _make_request(self, conn: HTTPConnection, method: str, url: str, body: Optional[Any] = None, headers: Optional[Mapping[str, str]] = None, retries: Optional[Retry] = None, timeout: Union[Timeout, float, None] = None, **response_kw: Any) -> BaseHTTPResponse:
        request_headers = {}
        if self.headers:
            request_headers.update(self.headers)
        if headers:
            request_headers.update(headers)
        conn.request(method, url, body, request_headers, timeout=timeout)
        response = conn.getresponse(**response_kw)
        return response

    def urlopen(self, method: str, url: str, body: Optional[Any] = None, headers: Optional[Mapping[str, str]] = None, retries: Optional[Retry] = None, redirect: bool = True, assert_same_host: bool = True, timeout: Union[Timeout, float, None] = None, pool_timeout: Optional[int] = None, **response_kw: Any) -> BaseHTTPResponse:
        conn = self._get_conn(pool_timeout)
        try:
            response = self._make_request(conn, method, url, body, headers, retries, timeout, **response_kw)
        except Exception as e:
            self._put_conn(conn)
            raise e
        return response

    def _close_pool_connections(self, pool: queue.LifoQueue) -> None:
        while not pool.empty():
            conn = pool.get_nowait()
            conn.close()

    def close(self) -> None:
        self._close_pool_connections(self.pool)

