import requests
import concurrent.futures

def query_dns(domain, record_type):
    def google_dns_query():
        url = f"https://dns.google/resolve?name={domain}&type={record_type}"
        try:
            response = requests.get(url, timeout=5)
            if response.status_code == 200:
                return response.json()
            return None
        except Exception:
            return None

    def cloudflare_dns_query():
        url = f"https://1.1.1.1/dns-query?name={domain}&type={record_type}"
        headers = {"accept": "application/dns-json"}
        try:
            response = requests.get(url, headers=headers, timeout=5)
            if response.status_code == 200:
                return response.json()
            return None
        except Exception:
            return None

    with concurrent.futures.ThreadPoolExecutor() as executor:
        future_google = executor.submit(google_dns_query)
        future_cloudflare = executor.submit(cloudflare_dns_query)

        return [future_google.result(), future_cloudflare.result()]

class AmazonSSO:
    def __init__(self, access_token: str = None, refresh_token: str = None):
        self.access_token = access_token
        self.refresh_token = refresh_token
        self.client_id = ""
        self.client_secret = ""
        self.user_pool_id = ""
        self.region = ""
        self.user_info = {}

    def get_new_token(self) -> str:
        import requests
        url = f"https://{self.user_pool_id}.auth.{self.region}.amazoncognito.com/oauth2/token"
        headers = {
            "Content-Type": "application/x-www-form-urlencoded"
        }
        data = {
            "grant_type": "refresh_token",
            "client_id": self.client_id,
            "refresh_token": self.refresh_token
        }
        response = requests.post(url, headers=headers, data=data, auth=(self.client_id, self.client_secret))
        if response.status_code == 200:
            tokens = response.json()
            self.access_token = tokens.get("access_token")
            self.refresh_token = tokens.get("refresh_token")
            return self.access_token
        return None

    def get_user_info(self) -> dict:
        import requests
        url = f"https://{self.user_pool_id}.auth.{self.region}.amazoncognito.com/oauth2/userInfo"
        headers = {
            "Authorization": f"Bearer {self.access_token}"
        }
        response = requests.get(url, headers=headers)
        if response.status_code == 200:
            self.user_info = response.json()
            return self.user_info
        return {}

    @staticmethod
    def amazon_sso(code: str, redirect_uri: str = None):
        import requests
        client_id = ""
        client_secret = ""
        user_pool_id = ""
        region = ""
        url = f"https://{user_pool_id}.auth.{region}.amazoncognito.com/oauth2/token"
        headers = {
            "Content-Type": "application/x-www-form-urlencoded"
        }
        data = {
            "grant_type": "authorization_code",
            "client_id": client_id,
            "code": code,
            "redirect_uri": redirect_uri
        }
        response = requests.post(url, headers=headers, data=data, auth=(client_id, client_secret))
        if response.status_code == 200:
            tokens = response.json()
            sso = AmazonSSO(access_token=tokens.get("access_token"), refresh_token=tokens.get("refresh_token"))
            return sso, None
        return None, None
