import urllib3
from .response_decoder import decode_response

MAX_FILE_SIZE = 20000000
MIN_FILE_SIZE = 10

LOGGER = logging.getLogger(__name__)
USER_AGENT = "Mozilla/5.0 (Macintosh; Intel Mac OS X 10_11_6) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/53.0.2785.143 Safari/537.36"
HEADERS = {
    "Connection": "close",
    "User-Agent": USER_AGENT,
}
urllib3.disable_warnings(urllib3.exceptions.InsecureRequestWarning)

class SimpleCrawler:
    _results = {}

    def __init__(self):
        pass

    def fetch_url(self, url, request_args=None):
        """
        Crawls the html content of the parameter url and returns the html
        :param url: URL to fetch
        :param request_args: optional arguments that `request` takes
        :return: HTML content of the URL
        """
        return self._fetch_url(url, False, request_args=request_args)

    def _fetch_url(self, url, is_threaded, request_args=None):
        """
        Crawls the html content of the parameter url and saves the html in _results
        :param url: URL to fetch
        :param is_threaded: If True, results will be stored for later processing
        :param request_args: optional arguments that `request` takes
        :return: html of the url
        """
        if request_args is None:
            request_args = {}
        if "headers" not in request_args:
            request_args["headers"] = HEADERS

        html_str = None
        try:
            response = requests.get(
                url, verify=False, allow_redirects=True, **request_args)

            if response.status_code != 200:
                LOGGER.error("not a 200 response: %s", response.status_code)
            elif response.text is None or len(response.text) < MIN_FILE_SIZE:
                LOGGER.error("too small/incorrect: %s %s", url, len(response.text))
            elif len(response.text) > MAX_FILE_SIZE:
                LOGGER.error("too large: %s %s", url, len(response.text))
            else:
                html_str = decode_response(response)

        except (requests.exceptions.MissingSchema, requests.exceptions.InvalidURL):
            LOGGER.error("malformed URL: %s", url)
        except requests.exceptions.TooManyRedirects:
            LOGGER.error("too many redirects: %s", url)
        except requests.exceptions.SSLError as err:
            LOGGER.error("SSL: %s %s", url, err)
        except (socket.timeout, requests.exceptions.ConnectionError, 
               requests.exceptions.Timeout, socket.error, socket.gaierror) as err:
            LOGGER.error("connection/timeout error: %s %s", url, err)

        if is_threaded:
            self._results[url] = html_str
        return html_str

    def fetch_urls(self, urls, request_args=None):
        """
        Crawls the html content of all given urls in parallel.
        :param urls: List of URLs to fetch
        :param request_args: optional arguments that `request` takes
        :return: Dictionary of URL to HTML content mappings
        """
        threads = [
            threading.Thread(target=self._fetch_url, args=(url, True, request_args))
            for url in urls
        ]
        for thread in threads:
            thread.start()
        for thread in threads:
            thread.join()

        results = copy.deepcopy(self._results)
        self._results = {}
        return results

