def download_schema_file(envs: Dict[str, Any]) -> Path:
    base_dir = Path(envs['GITHUB_WORKSPACE']).resolve()
    schema_file = envs.get('SIGMA_SCHEMA_FILE')
    if schema_file is None:
        local_path = base_dir / 'sigma-schema.json'
    else:
        if Path(schema_file).is_absolute():
            local_path = Path(schema_file).resolve()
        else:
            local_path = (base_dir / schema_file).resolve()

    if local_path.exists():
        return local_path

    url = envs['SIGMA_SCHEMA_URL']
    try:
        response = requests.get(url)
        response.raise_for_status()
    except requests.exceptions.RequestException as e:
        print(f"Warning: Failed to download schema file from {url}: {e}", file=sys.stderr)
        sys.exit(-1)

    local_path.parent.mkdir(parents=True, exist_ok=True)
    try:
        with open(local_path, 'wb') as f:
            f.write(response.content)
    except IOError as e:
        print(f"Warning: Failed to save schema file to {local_path}: {e}", file=sys.stderr)
        sys.exit(-1)

    return local_path

