import queue
from http.client import HTTPConnection, HTTPResponse
from urllib.parse import urlparse

class HTTPConnectionPool:
    def __init__(self, host, port=None, strict=False, timeout=object(), maxsize=1, block=False, headers=None, retries=None, _proxy=None, _proxy_headers=None, **conn_kw):
        self.scheme = 'http'
        self.ConnectionCls = HTTPConnection
        self.ResponseCls = HTTPResponse
        self.host = host
        self.port = port
        self.timeout = timeout
        self.retries = retries
        self.pool = queue.LifoQueue(maxsize)
        self.block = block
        self.proxy = _proxy
        self.proxy_headers = _proxy_headers or {}
        self.conn_kw = conn_kw
        self.strict = strict

    def _new_conn(self):
        if self.proxy:
            host, port = self.proxy.split(':', 1)
            port = int(port)
            conn = self.ConnectionCls(host=host, port=port, strict=self.strict, **self.conn_kw)
        else:
            conn = self.ConnectionCls(host=self.host, port=self.port, strict=self.strict, **self.conn_kw)
        return conn

    def _get_conn(self, timeout=None):
        try:
            return self.pool.get(block=True, timeout=timeout)
        except queue.Empty:
            return self._new_conn()

    def _put_conn(self, conn):
        try:
            self.pool.put(conn, block=self.block)
        except queue.Full:
            conn.close()

    def _make_request(self, conn, method, url, timeout=object(), **httplib_request_kw):
        if timeout is not object():
            conn.timeout = timeout
        conn.request(method, url, **httplib_request_kw)
        return self.ResponseCls(conn.getresponse())

    def urlopen(self, method, url, body=None, headers=None, retries=None, redirect=True, assert_same_host=True, timeout=object(), pool_timeout=None, release_conn=None, **response_kw):
        parsed_url = urlparse(url)
        if assert_same_host and (parsed_url.hostname != self.host or parsed_url.port != self.port):
            raise ValueError("Host mismatch")
        retries = retries or self.retries
        num_retries = getattr(retries, 'total', retries)
        while True:
            try:
                conn = self._get_conn(pool_timeout)
                resp = self._make_request(conn, method, url, timeout=timeout, **response_kw)
                break
            except Exception:
                if num_retries > 0:
                    num_retries -= 1
                    if conn:
                        self._put_conn(conn)
                else:
                    raise
        if release_conn is None or release_conn:
            self._put_conn(conn)
        return resp
