class LlamaStackServer:
    def __init__(self, config):
        self.config = config
        self.app = FastAPI(lifespan=self.lifespan)
        self.app.add_middleware(TracingMiddleware)
        self.impls = None

    @asynccontextmanager
    async def lifespan(self, app: FastAPI):
        print("Starting up")
        yield
        print("Shutting down")
        for impl in app.__llama_stack_impls__.values():
            await impl.shutdown()

    async def global_exception_handler(self, request: Request, exc: Exception):
        traceback.print_exception(exc)
        http_exc = self.translate_exception(exc)
        return JSONResponse(
            status_code=http_exc.status_code, 
            content={"error": {"detail": http_exc.detail}}
        )

    def translate_exception(self, exc: Exception) -> Union[HTTPException, RequestValidationError]:
        if isinstance(exc, ValidationError):
            exc = RequestValidationError(exc.raw_errors)

        if isinstance(exc, RequestValidationError):
            return HTTPException(
                status_code=400,
                detail={
                    "errors": [
                        {
                            "loc": list(error["loc"]),
                            "msg": error["msg"],
                            "type": error["type"],
                        }
                        for error in exc.errors()
                    ]
                },
            )
        elif isinstance(exc, ValueError):
            return HTTPException(status_code=400, detail=f"Invalid value: {str(exc)}")
        elif isinstance(exc, PermissionError):
            return HTTPException(status_code=403, detail=f"Permission denied: {str(exc)}")
        elif isinstance(exc, TimeoutError):
            return HTTPException(status_code=504, detail=f"Operation timed out: {str(exc)}")
        elif isinstance(exc, NotImplementedError):
            return HTTPException(status_code=501, detail=f"Not implemented: {str(exc)}")
        else:
            return HTTPException(
                status_code=500,
                detail="Internal server error: An unexpected error occurred.",
            )

    def create_dynamic_typed_route(self, func: Any, method: str):
        async def endpoint(request: Request, **kwargs):
            set_request_provider_data(request.headers)

            is_streaming = self.is_streaming_request(func.__name__, request, **kwargs)
            try:
                if is_streaming:
                    return StreamingResponse(
                        self.sse_generator(func(**kwargs)), media_type="text/event-stream"
                    )
                else:
                    value = func(**kwargs)
                    return await self.maybe_await(value)
            except Exception as e:
                traceback.print_exception(e)
                raise self.translate_exception(e) from e

        sig = inspect.signature(func)
        new_params = [
            inspect.Parameter(
                "request", inspect.Parameter.POSITIONAL_OR_KEYWORD, annotation=Request
            )
        ]
        new_params.extend(sig.parameters.values())

        if method == "post":
            new_params = [new_params[0]] + [
                param.replace(annotation=Annotated[param.annotation, Body(..., embed=True)])
                for param in new_params[1:]
            ]

        endpoint.__signature__ = sig.replace(parameters=new_params)
        return endpoint

    def is_streaming_request(self, func_name: str, request: Request, **kwargs):
        return kwargs.get("stream", False)

    async def maybe_await(self, value):
        if inspect.iscoroutine(value):
            return await value
        return value

    async def sse_generator(self, event_gen):
        try:
            event_gen = await event_gen
            async for item in event_gen:
                yield self.create_sse_event(item)
                await asyncio.sleep(0.01)
        except asyncio.CancelledError:
            print("Generator cancelled")
            await event_gen.aclose()
        except Exception as e:
            traceback.print_exception(e)
            yield self.create_sse_event(
                {
                    "error": {
                        "message": str(self.translate_exception(e)),
                    },
                }
            )

    def create_sse_event(self, data: Any) -> str:
        if isinstance(data, BaseModel):
            data = data.model_dump_json()
        else:
            data = json.dumps(data)
        return f"data: {data}\n\n"

