                params=params,
                json=param,
                proxies=proxies
            )
            response.raise_for_status()
            return response.json()
        except requests.exceptions.RequestException:
            return {'error': 'Request failed'}

    def get_zone_id(self, domain):
        parts = domain.split('.')
        for i in range(len(parts)):
            suffix = '.'.join(parts[i:])
            response = self._request("GET", "zones", **{"name": suffix, "per_page": 100})
            if response.get('success', False):
                for zone in response.get('result', []):
                    if zone['name'] == suffix:
                        return zone['id']
        return None

    def get_records(self, zoneid, **conditions):
        now = time.time()
        if zoneid in self._records_cache:
            cached_time, records = self._records_cache[zoneid]
            if now - cached_time < self.TTL:
                return records
        response = self._request("GET", f"zones/{zoneid}/dns_records", **conditions)
        if not response.get('success', False):
            return []
        records = response.get('result', [])
        self._records_cache[zoneid] = (now, records)
        return records

    def update_record(self, domain, value, record_type="A"):
        zoneid = self.get_zone_id(domain)
        if not zoneid:
            return {'error': 'Zone not found'}
        conditions = {"name": domain, "type": record_type}
        records = self.get_records(zoneid, **conditions)
        if records:
            record = records[0]
            record_id = record['id']
            data = {
                "type": record_type,
                "name": domain,
                "content": value,
                "ttl": self.TTL,
                "proxied": record.get('proxied', False)
            }
            response = self._request("PUT", f"zones/{zoneid}/dns_records/{record_id}", param=data)
            return response
        else:
            data = {
                "type": record_type,
                "name": domain,
                "content": value,
                "ttl": self.TTL,
                "proxied": False
            }
            response = self._request("POST", f"zones/{zoneid}/dns_records", param=data)
            return response
