import urllib.parse
from queue import LifoQueue

class HTTPConnectionPool:
    def __init__(self, host, port=None, strict=False, timeout="Default", maxsize=1, block=False, headers=None, retries=None, _proxy=None, _proxy_headers=None, **conn_kw):
        self.scheme = 'http'
        self.host = host
        self.port = port or 80
        self.timeout = timeout
        self.retries = retries
        self.pool = LifoQueue(maxsize)
        self.block = block
        self.proxy = _proxy
        self.proxy_headers = _proxy_headers or {}

    def _new_conn(self):
        if self.proxy:
            parsed_proxy = urllib.parse.urlparse(self.proxy)
            proxy_host, proxy_port = parsed_proxy.hostname, parsed_proxy.port
            if not proxy_port:
                proxy_port = 80 if parsed_proxy.scheme == 'http' else 443
            conn = http.client.HTTPConnection(proxy_host, proxy_port)
        else:
            conn = http.client.HTTPConnection(self.host, self.port)
        return conn

    def _get_conn(self, timeout=None):
        try:
            return self.pool.get(block=self.block, timeout=timeout)
        except:
            return self._new_conn()

    def _put_conn(self, conn):
        try:
            self.pool.put(conn, block=False)
        except:
            conn.close()

    def _make_request(self, conn, method, url, timeout=None, **httplib_request_kw):
        if self.proxy:
            full_url = f"{self.scheme}://{self.host}:{self.port}{url}"
            conn.request(method, full_url, headers=self.proxy_headers, **httplib_request_kw)
        else:
            conn.request(method, url, **httplib_request_kw)
        return conn.getresponse()

    def urlopen(self, method, url, body=None, headers=None, retries=None, redirect=True, assert_same_host=True, timeout=None, pool_timeout=None, release_conn=None, **response_kw):
        conn = self._get_conn(timeout=pool_timeout)
        try:
            response = self._make_request(conn, method, url, timeout=timeout, body=body, headers=headers)
            if release_conn is None:
                self._put_conn(conn)
            else:
                if not release_conn:
                    conn.close()
                else:
                    self._put_conn(conn)
        except:
            if retries:
                pass
            else:
                raise
        return response

    def close(self):
        while not self.pool.empty():
            self.pool.get_nowait().close()

class HTTPSConnectionPool(HTTPConnectionPool):
    def __init__(self, host, port=None, strict=False, timeout="Default", maxsize=1, block=False, headers=None, retries=None, _proxy=None, _proxy_headers=None, key_file=None, cert_file=None, cert_reqs=None, ca_certs=None, ssl_version=None, assert_hostname=None, assert_fingerprint=None, **conn_kw):
        super().__init__(host, port, strict, timeout, maxsize, block, headers, retries, _proxy, _proxy_headers, **conn_kw)
        self.scheme = 'https'
        self.port = port or 443
        self.key_file = key_file
        self.cert_file = cert_file
        self.cert_reqs = cert_reqs
        self.ca_certs = ca_certs
        self.ssl_version = ssl_version
        self.assert_hostname = assert_hostname
        self.assert_fingerprint = assert_fingerprint

    def _prepare_conn(self, conn):
        context = ssl.create_default_context()
        if self.cert_reqs:
            context.verify_mode = self.cert_reqs
        if self.ca_certs:
            context.load_verify_locations(cafile=self.ca_certs)
        if self.key_file or self.cert_file:
            context.load_cert_chain(self.key_file, self.cert_file)
        conn._set_ssl_context(context)

    def _new_conn(self):
        context = ssl.create_default_context()
        if self.cert_reqs:
            context.verify_mode = self.cert_reqs
        if self.ca_certs:
            context.load_verify_locations(cafile=self.ca_certs)
        if self.key_file or self.cert_file:
            context.load_cert_chain(self.key_file, self.cert_file)
        conn = http.client.HTTPSConnection(self.host, self.port, context=context)
        self._prepare_conn(conn)
        return conn
