class StaticServer(BaseHTTPRequestHandler):
    def __init__(self, data, prof_filename, *args, **kwargs):
        self.data = data
        self.prof_filename = prof_filename
        super().__init__(*args, **kwargs)

    def do_GET(self):
        self.send_response(200)

        if self.path == "/":
            self.send_header("Content-type", "text/html")
            self.end_headers()
            self.wfile.write(self._render().encode())
        else:
            this_dir = Path(__file__).resolve().parent
            filepath = this_dir / "web" / self.path[1:]

            mimetype, _ = mimetypes.guess_type(str(filepath))
            assert mimetype is not None
            self.send_header("Content-type", mimetype)
            self.end_headers()

            with open(filepath, "rb") as fh:
                content = fh.read()
            self.wfile.write(content)

    def _render(self):
        this_dir = Path(__file__).resolve().parent
        with open(this_dir / "web" / "index.html", encoding="utf-8") as _file:
            template = string.Template(_file.read())

        return template.substitute(
            data=html.escape(json.dumps(self.data).replace("</", "<\\/")),
            version=html.escape(__version__),
            filename=html.escape(self.prof_filename.replace("</", "<\\/")),
        )

