import http.client
from urllib.parse import urlparse
import time

class Timeout:
    DEFAULT_TIMEOUT = object()

class Retry:
    pass

class HTTPConnectionPool:
    def __init__(self, host, port=None, strict=False, timeout=Timeout.DEFAULT_TIMEOUT, maxsize=1, block=False, headers=None, retries=None, _proxy=None, _proxy_headers=None, **conn_kw):
        self.scheme = 'https' if (port == 443) else 'http'
        self.host = host
        self.port = port
        self.timeout = timeout
        self.retries = retries
        self.proxy = _proxy
        self.proxy_headers = _proxy_headers or {}
        self.conn_kw = conn_kw
        if _proxy:
            parsed_proxy = urlparse(_proxy)
            self.proxy_host = parsed_proxy.hostname
            self.proxy_port = parsed_proxy.port or (443 if parsed_proxy.scheme == 'https' else 80)
            self.proxy_scheme = parsed_proxy.scheme
        else:
            self.proxy_host = None
            self.proxy_port = None
            self.proxy_scheme = None
        self.headers = headers or {}
        self.pool = []
        self.maxsize = maxsize
        self.block = block

    def _new_conn(self):
        if self.proxy_host:
            if self.proxy_scheme == 'https':
                conn = http.client.HTTPSConnection(self.proxy_host, self.proxy_port, **self.conn_kw)
            else:
                conn = http.client.HTTPConnection(self.proxy_host, self.proxy_port, **self.conn_kw)
        else:
            if self.scheme == 'https':
                conn = http.client.HTTPSConnection(self.host, self.port, **self.conn_kw)
            else:
                conn = http.client.HTTPConnection(self.host, self.port, **self.conn_kw)
        return conn

    def _make_request(self, conn, method, url, timeout=Timeout.DEFAULT_TIMEOUT, chunked=False, **httplib_request_kw):
        if self.proxy_host:
            if self.scheme == 'https':
                conn.putrequest('CONNECT', f'{self.host}:{self.port}')
                for key, value in self.proxy_headers.items():
                    conn.putheader(key, value)
                conn.endheaders()
                response = conn.getresponse()
                if response.status != 200:
                    raise Exception("Proxy connection failed")
            else:
                full_url = f'{self.scheme}://{self.host}:{self.port}{url}'
                conn.request(method, full_url, **httplib_request_kw)
        else:
            conn.request(method, url, **httplib_request_kw)
        response = conn.getresponse()
        return response

    def _get_connection(self, pool_timeout=None):
        for conn in self.pool:
            self.pool.remove(conn)
            return conn
        if len(self.pool) < self.maxsize:
            return self._new_conn()
        if self.block:
            pass
        else:
            raise Exception("No connection available")

    def _release_connection(self, conn):
        self.pool.append(conn)

    def urlopen(self, method, url, body=None, headers=None, retries=None, redirect=True, assert_same_host=True, timeout=Timeout.DEFAULT_TIMEOUT, pool_timeout=None, release_conn=None, chunked=False, **response_kw):
        retry_count = 0
        while retry_count <= (retries if retries is not None else self.retries):
            try:
                conn = self._get_connection(pool_timeout)
                try:
                    req_headers = self.headers.copy()
                    if headers:
                        req_headers.update(headers)
                    response = self._make_request(conn, method, url, timeout=timeout, chunked=chunked, **response_kw)
                    if redirect and response.status in (301, 302, 303, 307, 308):
                        location = response.getheader('Location')
                        parsed_location = urlparse(location)
                        if assert_same_host and (parsed_location.hostname != self.host or parsed_location.port != self.port):
                            raise Exception("Redirect to different host")
                        return self.urlopen(method, location, body, headers, retries, redirect, timeout, pool_timeout, release_conn, chunked, **response_kw)
                    return response
                finally:
                    if release_conn is None or release_conn:
                        self._release_connection(conn)
                break
            except Exception as e:
                retry_count += 1
                if retry_count > (retries if retries is not None else self.retries):
                    raise e
                time.sleep(1)
        raise Exception("Request failed after retries")
