                if ports_part:
                    ports_str = ports_part.group(1)
                    ports = ports_str.split(', ')
                    for p in ports:
                        port_info = re.match(r'(\d+)/tcp/open', p)
                        if port_info:
                            open_ports.add(port_info.group(1))
        return list(open_ports)

    def useMasscanUDP(self) -> list:
        command = ['masscan', '--host', self.host, '--ports', '1-65535', '-pU', '-oG', '-']
