    def __init__(self, host, connection_timeout=60, keep_alive=True, logger=None, is_https=False):
        self.host = host
        self.is_https = is_https
        self.connection_timeout = connection_timeout
        self.keep_alive = keep_alive
        self.request_size = 0
        self.response_size = 0
        self.logger = logger
        self.proxy = None
        self.conn = None
        self._parse_host(host)
        if not self.proxy:
            self.proxy = self.get_env_proxy()
        self.conn = self._new_conn()

    def _parse_host(self, host_str):
        if ':' in host_str:
            self.host, port_str = host_str.split(':', 1)
            self.port = int(port_str)
        else:
            self.host = host_str
            self.port = 443 if self.is_https else 80

    def _new_conn(self):
        if self.proxy:
            parsed_proxy = urlparse(self.proxy)
            proxy_host = parsed_proxy.hostname
            proxy_port = parsed_proxy.port or 8080
            if self.is_https:
                conn = MNSHTTPSConnection(proxy_host, proxy_port, connection_timeout=self.connection_timeout)
            else:
                conn = MNSHTTPConnection(proxy_host, proxy_port, connection_timeout=self.connection_timeout)
            if self.is_https:
                conn.set_tunnel(self.host, self.port)
        else:
            if self.is_https:
                conn = MNSHTTPSConnection(self.host, self.port, connection_timeout=self.connection_timeout)
            else:
                conn = MNSHTTPConnection(self.host, self.port, connection_timeout=self.connection_timeout)
        return conn

    def _basic_auth_str(self, username, password):
        credentials = f'{username}:{password}'
        encoded = base64.b64encode(credentials.encode('utf-8')).decode('utf-8')
        return f'Basic {encoded}'

    def get_env_proxy(self):
        if self.is_https:
            proxy_env = os.environ.get('HTTPS_PROXY') or os.environ.get('https_proxy')
        else:
            proxy_env = os.environ.get('HTTP_PROXY') or os.environ.get('http_proxy')
        return proxy_env

    def set_connection_timeout(self, connection_timeout):
        self.connection_timeout = connection_timeout
        self.conn = self._new_conn()

    def send_request(self, req_inter):
        if not self.conn or not hasattr(self.conn, 'sock') or not self.conn.sock:
            self.conn = self._new_conn()
        headers = req_inter.headers.copy()
        if hasattr(req_inter, 'username') and hasattr(req_inter, 'password'):
            headers['Authorization'] = self._basic_auth_str(req_inter.username, req_inter.password)
        if not self.keep_alive:
            headers['Connection'] = 'close'
        try:
            self.conn.request(req_inter.method, req_inter.path, body=req_inter.body, headers=headers)
        except Exception as e:
            if self.logger:
                self.logger.error(f"Request failed: {e}")
            raise
        response = self.conn.getresponse()
        response_data = response.read()
        self.response_size = len(response_data)
        self.request_size = self.conn.request_length
        return ResponseInternal(status=response.status, headers=dict(response.getheaders()), body=response_data)

class MNSHTTPConnection(http.client.HTTPConnection):
    def __init__(self, host, port=None, strict=None, connection_timeout=60):
        super().__init__(host, port, strict)
        self.connection_timeout = connection_timeout
        self.request_length = 0

    def connect(self):
        sock = socket.create_connection((self.host, self.port), timeout=self.connection_timeout)
        self.sock = sock

    def send(self, data):
        if self._tunnel_host:
            self._tunnel()
        sent = super().send(data)
        self.request_length += sent
        return sent

    def request(self, method, url, body=None, headers={}):
        self.request_length = 0
        super().request(method, url, body, headers)

class MNSHTTPSConnection(http.client.HTTPSConnection):
    def __init__(self, host, port=None, strict=None, connection_timeout=60):
        super().__init__(host, port, strict)
        self.connection_timeout = connection_timeout
        self.request_length = 0

    def connect(self):
        sock = socket.create_connection((self.host, self.port), timeout=self.connection_timeout)
        if getattr(self, '_tunnel_host', None):
            self._tunnel()
        self.sock = ssl.wrap_socket(sock)

    def send(self, data):
        if self._tunnel_host:
            self._tunnel()
        sent = super().send(data)
        self.request_length += sent
        return sent

    def request(self, method, url, body=None, headers={}):
        self.request_length = 0
        super().request(method, url, body, headers)

class ResponseInternal:
    def __init__(self, status, headers, body):
        self.status = status
        self.headers = headers
        self.body = body

