import subprocess

def _get_hg_branch():
    result = subprocess.run(['hg', 'branch'], capture_output=True, text=True)
    if result.returncode != 0:
        return None
    return result.stdout.strip()

def _get_hg_status(output):
    stdout, stderr = output
    return [line.strip() for line in stdout.splitlines() if line.strip()]

def build_stats():
    branch = _get_hg_branch()
    if branch is None:
        return (None, None)
    status_result = subprocess.run(['hg', 'status'], capture_output=True, text=True)
    if status_result.returncode != 0:
        return (None, None)
    status_list = _get_hg_status((status_result.stdout, status_result.stderr))
    return (status_list, branch)

