    def get_domain_info(self, domain):
        labels = domain.split('.')
        if len(labels) < 2:
            raise ValueError("Invalid domain")
        main_domain = '.'.join(labels[-2:])
        sub_domain = '.'.join(labels[:-2]) if len(labels) > 2 else ''
        return main_domain, sub_domain

    def get_domain_id(self, domain):
        if domain in self.domain_list:
            return self.domain_list[domain]
        result = self.request('Domain.List', {'domain': domain})
        for d in result['domains']:
            if d['name'] == domain:
                self.domain_list[domain] = d['id']
                return d['id']
        raise ValueError(f"Domain {domain} not found")

    def get_records(self, did, **conditions):
        if did in self.records:
            cached_records = self.records[did]
        else:
            result = self.request('Record.List', {'domain_id': did})
            self.records[did] = result['records']
            cached_records = self.records[did]
        filtered = []
        for record in cached_records:
            match = True
            for key, value in conditions.items():
                if record.get(key) != value:
                    match = False
                    break
            if match:
                filtered.append(record)
        return {'records': filtered}

    def update_record(self, domain, value, record_type="A"):
        main_domain, sub_domain = self.get_domain_info(domain)
        did = self.get_domain_id(main_domain)
        records = self.get_records(did, name=sub_domain, type=record_type)
        if records['records']:
            record = records['records'][0]
            record_id = record['id']
            params = {
                'domain_id': did,
                'record_id': record_id,
                'sub_domain': sub_domain,
                'record_type': record_type,
                'value': value,
                'ttl': self.TTL or record['ttl'],
                'record_line': self.DEFAULT
            }
            return self.request('Record.Modify', params)
        else:
            params = {
                'domain_id': did,
                'sub_domain': sub_domain,
                'record_type': record_type,
                'value': value,
                'ttl': self.TTL or 600,
                'record_line': self.DEFAULT
            }
            return self.request('Record.Create', params)

