        if proxy_type == socket.SOCKS4:
            self._negotiate_SOCKS4(dest_host, dest_port, username)
        elif proxy_type == socket.SOCKS5:
            self._negotiate_SOCKS5(dest_host, dest_port, rdns, username, password)
        elif proxy_type == 'HTTP':
            self._negotiate_HTTP(dest_host, dest_port)
        else:
            raise ValueError("Unsupported proxy type")

        self.proxy_peername = dest_pair

    def _readall(self, count):
        data = b''
        while len(data) < count:
            chunk = self.recv(count - len(data))
            if not chunk:
                raise EOFError("Unexpected EOF")
            data += chunk
        return data

    def _negotiate_SOCKS4(self, dest_host, dest_port, username):
        dest_ip = socket.inet_aton(socket.gethostbyname(dest_host))
        userid = (username or '').encode('ascii') + b'\x00'
        request = struct.pack('!BBH4s', 4, 1, dest_port, dest_ip) + userid
        self.sendall(request)

        response = self._readall(8)
        if len(response) < 8:
            raise socket.error("SOCKS4: truncated response")
        ver, status, port, ip = struct.unpack('!BBH4s', response)
        if status != 0x5a:
            raise socket.error(f"SOCKS4: request failed {status}")
        self.proxy_sockname = (socket.inet_ntoa(ip), port)

    def _negotiate_SOCKS5(self, dest_host, dest_port, rdns, username, password):
        self.sendall(struct.pack('!BB', 5, 1) + b'\x00')
        methods = self._readall(2)
        if len(methods) < 2:
            raise socket.error("SOCKS5: no method selected")
        ver, method = struct.unpack('!BB', methods)
        if ver != 5:
            raise socket.error("SOCKS5: invalid version in method selection")

        if method == 0x02:
            auth = struct.pack('!BB', 1, len(username)) + username.encode('ascii') + struct.pack('!B', len(password)) + password.encode('ascii')
            self.sendall(auth)
            auth_resp = self._readall(2)
            if len(auth_resp) < 2:
                raise socket.error("SOCKS5: authentication failed")
            ver_auth, status_auth = struct.unpack('!BB', auth_resp)
            if status_auth != 0x00:
                raise socket.error("SOCKS5: authentication failed")

        if rdns:
            domain = dest_host
            domain_len = len(domain)
            atyp = 3
            request = struct.pack('!BBB', 5, 1, 0) + bytes([atyp, domain_len]) + domain.encode('ascii') + struct.pack('!H', dest_port)
        else:
            ip = socket.inet_aton(socket.gethostbyname(dest_host))
            atyp = 1
            request = struct.pack('!BBB', 5, 1, 0) + bytes([atyp]) + ip + struct.pack('!H', dest_port)
        self.sendall(request)

        response = self._readall(4)
        if len(response) < 4:
            raise socket.error("SOCKS5: truncated response")
        ver, status, rsv, atyp = struct.unpack('!BBBB', response)
        if status != 0x00:
            raise socket.error(f"SOCKS5: request failed {status}")

        if atyp == 1:
            ip = self._readall(4)
            port = struct.unpack('!H', self._readall(2))[0]
            self.proxy_sockname = (socket.inet_ntoa(ip), port)
        elif atyp == 3:
            domain_len = ord(self._readall(1))
            domain = self._readall(domain_len)
            port = struct.unpack('!H', self._readall(2))[0]
            self.proxy_sockname = (domain.decode('ascii'), port)
        else:
            raise socket.error("SOCKS5: invalid address type")

    def _negotiate_HTTP(self, dest_host, dest_port):
        request = f"CONNECT {dest_host}:{dest_port} HTTP/1.1\r\nHost: {dest_host}:{dest_port}\r\n\r\n"
        self.sendall(request.encode('ascii'))

        response_line = b''
        while True:
            char = self.recv(1)
            if not char or char == b'\n':
                break
            response_line += char
        response_line = response_line.strip(b'\r')
        if not response_line.startswith(b'HTTP/1.1 200'):
            raise socket.error(f"HTTP proxy: {response_line.decode('ascii')}")

