        headers = {
            "Content-Type": "application/json",
            "key": str(self._api_key),
            "modelName": self.model_type,
        }

        data = {
            "instance": json.dumps({
                "conversation_id": str(uuid.uuid4()),
                "messages": messages,
            }),
            "params": {
                "do_sample": {"type": "bool", "value": "true"},
                "max_tokens_to_generate": {
                    "type": "int",
                    "value": str(self.model_config_dict.get("max_tokens")),
                },
                "process_prompt": {"type": "bool", "value": "true"},
                "repetition_penalty": {
                    "type": "float",
                    "value": str(self.model_config_dict.get("repetition_penalty")),
                },
                "return_token_count_only": {"type": "bool", "value": "false"},
                "select_expert": {
                    "type": "str",
                    "value": self.model_type.split('/')[1],
                },
                "stop_sequences": {
                    "type": "str",
                    "value": self.model_config_dict.get("stop_sequences"),
                },
                "temperature": {
                    "type": "float",
                    "value": str(self.model_config_dict.get("temperature")),
                },
                "top_k": {
                    "type": "int",
                    "value": str(self.model_config_dict.get("top_k")),
                },
                "top_p": {
                    "type": "float",
                    "value": str(self.model_config_dict.get("top_p")),
                },
            },
        }

        with httpx.Client() as client:
            response = client.post(
                self._url,
                headers=headers,
                json=data,
            )

        raw_text = response.text
        dicts = raw_text.split('}\n{')
        last_dict = '{' + dicts[-1]
        last_dict = json.loads(last_dict)
        return self._convert_response(last_dict)

    def _convert_response(self, samba_response: Dict[str, Any]) -> ChatCompletion:
        choices = [
            dict(
                index=0,
                message={
                    "role": 'assistant',
                    "content": samba_response['result']['responses'][0]['completion'],
                },
                finish_reason=samba_response['result']['responses'][0]['stop_reason'],
            )
        ]

        return ChatCompletion.construct(
            id=None,
            choices=choices,
            created=int(time.time()),
            model=self.model_type,
            object="chat.completion",
            usage=CompletionUsage(
                completion_tokens=0,
                prompt_tokens=0,
                total_tokens=int(samba_response['result']['responses'][0]['total_tokens_count']),
            ),
        )

