                sock.connect(host)
            else:
                sock = socket.socket(socket.AF_INET, socket.SOCK_STREAM)
                sock.connect((host, int(port)))

            stats_data = ''
            while True:
                data = sock.recv(1024)
                if not data:
                    break
                stats_data += data
            sock.close()

