import xml.etree.ElementTree
import socket
import binascii
import time

def load_ports():
    ports = {}
    dir = os.path.dirname(os.path.abspath(__file__))
    root = xml.etree.ElementTree.parse(dir + '/../setup/default.xml').getroot()
    for entry in root.findall('entry'):
        key = entry.attrib['key']
        if key.endswith('.port'):
            ports[key[:-5]] = int(entry.text)
    return ports

def send_messages_to_server(path):
    p = re.compile(r"\d{4}-\d{2}-\d{2} \d{2}:\d{2}:\d{2}  INFO: \[([TU][0-9a-fA-F]{8}): (\S+) < [\d.]+] ([0-9a-fA-F]+)")
    ports = load_ports()
    protocols = {}
    messages = {}

    for line in open(path):
        m = p.match(line)
        if m:
            session = m.group(1)
            protocol = m.group(2)
            message = m.group(3)
            protocols[session] = protocol
            if session not in messages:
                messages[session] = []
            messages[session].append(message)

    for session in protocols:
        port = ports[protocols[session]]
        s = socket.socket(socket.AF_INET, socket.SOCK_STREAM)
        s.connect(("localhost", int(port)))
        for message in messages[session]:
            s.send(binascii.unhexlify(message))
            time.sleep(0.1)
        s.close()

import json
from typing import Generator
import httpx
from httpx_sse import SSEError, connect_sse
from letta.constants import OPENAI_CONTEXT_WINDOW_ERROR_SUBSTRING
from letta.errors import LLMError
from letta.schemas.enums import MessageStreamStatus
from letta.schemas.letta_message import ReasoningMessage, ToolCallMessage, ToolReturnMessage
from letta.schemas.letta_response import LettaStreamingResponse
from letta.schemas.usage import LettaUsageStatistics

def _sse_post(url: str, data: dict, headers: dict) -> Generator[LettaStreamingResponse, None, None]:
    with httpx.Client() as client:
        with connect_sse(client, method="POST", url=url, json=data, headers=headers) as event_source:
            if not event_source.response.is_success:
                response_bytes = event_source.response.read()
                response_dict = json.loads(response_bytes.decode("utf-8"))
                error_message = response_dict["error"]["message"]
                if OPENAI_CONTEXT_WINDOW_ERROR_SUBSTRING in error_message:
                    raise LLMError(error_message)
                event_source.response.raise_for_status()

            try:
                for sse in event_source.iter_sse():
                    if sse.data in [status.value for status in MessageStreamStatus]:
                        yield MessageStreamStatus(sse.data)
                    else:
                        chunk_data = json.loads(sse.data)
                        if "reasoning" in chunk_data:
                            yield ReasoningMessage(**chunk_data)
                        elif "tool_call" in chunk_data:
                            yield ToolCallMessage(**chunk_data)
                        elif "tool_return" in chunk_data:
                            yield ToolReturnMessage(**chunk_data)
                        elif "step_count" in chunk_data:
                            yield LettaUsageStatistics(**chunk_data)
                        else:
                            raise ValueError(f"Unknown message type in chunk_data: {chunk_data}")

            except SSEError as e:
                if "application/json" in str(e):
                    response = client.post(url=url, json=data, headers=headers)
                    if response.headers["Content-Type"].startswith("application/json"):
                        error_details = response.json()
                raise e

            except Exception as e:
                raise e

