        if self.debug_mode:
            logging.info("Control connection established")
        if self.connection_established.is_set():
            return
        if self.data_ws and self.data_ws.sock and self.data_ws.sock.connected:
            self.connection_established.set()

    def on_data_open(self, ws: WebSocketApp) -> None:
        if self.debug_mode:
            logging.info("Data connection established")
        if self.connection_established.is_set():
            return
        if self.control_ws and self.control_ws.sock and self.control_ws.sock.connected:
            self.connection_established.set()

    def set_parameter(self, parameter: str, value: Any) -> None:
        if self.control_ws and self.control_ws.sock and self.control_ws.sock.connected:
            req_id = self.request_counter
            self.request_counter += 1
            message = {
                "type": "set_parameter",
                "parameter": parameter,
                "value": value,
                "request_id": req_id
            }
            self.control_ws.send(json.dumps(message))

    def get_parameter(self, parameter: str) -> Optional[Any]:
        if not self.control_ws or not self.control_ws.sock or not self.control_ws.sock.connected:
            return None

        req_id = self.request_counter
        self.request_counter += 1
        result = threading.Event()
        value = None

        def set_value(val):
            nonlocal value
            value = val
            result.set()

        with self.pending_lock:
            self.pending_requests[req_id] = (set_value, req_id)

        message = {
            "type": "get_parameter",
            "parameter": parameter,
            "request_id": req_id
        }
        self.control_ws.send(json.dumps(message))

        result.wait(timeout=5)
        return value

    def call_method(self, method: str, args: Optional[List] = None, kwargs: Optional[Dict] = None) -> None:
        if self.control_ws and self.control_ws.sock and self.control_ws.sock.connected:
            req_id = self.request_counter
            self.request_counter += 1
            message = {
                "type": "call_method",
                "method": method,
                "args": args or [],
                "kwargs": kwargs or {},
                "request_id": req_id
            }
            self.control_ws.send(json.dumps(message))

    def shutdown(self) -> None:
        self.is_running = False
        if self.control_ws:
            self.control_ws.close()
        if self.data_ws:
            self.data_ws.close()
        self.connection_established.clear()

