import asyncio
import websockets
import json

async def server(websocket, path):
    async for message in websocket:
        try:
            data = json.loads(message)
        except json.JSONDecodeError:
            await websocket.send(json.dumps({'throw': 'Invalid JSON'}))
            continue
        func = data.get('function')
        args = data.get('args', [])
        try:
            if not func:
                raise ValueError("Missing 'function' field")
            if func not in ['echo', 'ask', 'throw']:
                raise ValueError(f"Unknown function: {func}")
            if func == 'echo':
                result = args[0] if args else ''
            elif func == 'ask':
                result = ''.join(args)
            elif func == 'throw':
                error_msg = args[0] if args else 'No error message provided'
                await websocket.send(json.dumps({'throw': error_msg}))
                continue
            response = {'return': result}
            await websocket.send(json.dumps(response))
        except Exception as e:
            await websocket.send(json.dumps({'throw': str(e)}))

async def start_websocket_server():
    async with websockets.serve(server, "localhost", 8765):
        await asyncio.get_event_loop().create_future()

