import requests
import os
from tqdm import tqdm

def download_market_data(exchange, data_type, symbol, yyyymmdd, filepath, key):
    date_str = str(yyyymmdd)
    url = f"https://api.example.com/data/{exchange}/{data_type}/{symbol}/{date_str}.csv.gz"
    headers = {"Authorization": f"Bearer {key}"}

    try:
        response = requests.get(url, headers=headers, stream=True)
        response.raise_for_status()
    except requests.exceptions.RequestException as e:
        raise RuntimeError(f"API request failed: {str(e)}") from e

    total_size = int(response.headers.get("Content-Length", 0))
    try:
        with open(filepath, "wb") as file:
            downloaded_size = 0
            with tqdm(total=total_size, unit="iB", unit_scale=True, desc=filepath) as pbar:
                for chunk in response.iter_content(chunk_size=8192):
                    if chunk:
                        file.write(chunk)
                        downloaded_size += len(chunk)
                        pbar.update(len(chunk))
    except Exception as e:
        response.close()
        if os.path.exists(filepath):
            os.remove(filepath)
        raise RuntimeError(f"File write failed: {str(e)}") from e

    if os.path.getsize(filepath) != total_size:
        os.remove(filepath)
        raise RuntimeError("Download verification failed - file size mismatch")

import requests
from firebase_admin import credentials, storage, initialize_app

class ImageGeneratorAndUploader:
    def __init__(self, hugging_face_api_url, hugging_face_headers, firebase_creds_path, firebase_storage_bucket):
        self.HUGGING_FACE_API_URL = hugging_face_api_url
        self.headers = hugging_face_headers
        self.FIREBASE_STORAGE_BUCKET = firebase_storage_bucket
        try:
            firebase_admin.get_app()
        except:
            cred = credentials.Certificate(firebase_creds_path)
            initialize_app(cred, {'storageBucket': firebase_storage_bucket})

    def query(self, payload):
        try:
            response = requests.post(self.HUGGING_FACE_API_URL, headers=self.headers, json=payload)
            response.raise_for_status()
            return response.content
        except requests.exceptions.RequestException:
            return None

    def upload_to_firebase_storage(self, image_data, destination_path):
        try:
            bucket = storage.bucket(self.FIREBASE_STORAGE_BUCKET)
            blob = bucket.blob(destination_path)
            blob.upload_from_string(image_data, content_type='image/png')
            return blob.public_url
        except Exception:
            return None

    def generate_and_store_image(self, prompt, destination_path):
        payload = {"inputs": prompt}
        image_data = self.query(payload)
        if not image_data:
            return None
        return self.upload_to_firebase_storage(image_data, destination_path)

