import threading
from http.client import HTTPConnection, HTTPResponse


class HTTPConnectionPool:
    def __init__(self, host, port=None, strict=False, timeout=..., maxsize=1, block=False, headers=None, retries=None, _proxy=None, _proxy_headers=None, **conn_kw):
        self.scheme = 'http'
        self.ConnectionCls = HTTPConnection
        self.ResponseCls = HTTPResponse
        self.host = host
        self.port = port
        self.timeout = timeout
        self.retries = retries
        self.pool = queue.LifoQueue(maxsize=maxsize)
        self.block = block
        self.proxy = _proxy
        self.proxy_headers = _proxy_headers or {}
        self.num_connections = 0
        self.lock = threading.Lock()
        self.conn_kw = conn_kw

    def _new_conn(self):
        extra_kw = {}
        if self.timeout:
            extra_kw['timeout'] = self.timeout
        extra_kw.update(self.conn_kw)
        return self.ConnectionCls(self.host, port=self.port, **extra_kw)

    def _get_conn(self, timeout=None):
        try:
            conn = self.pool.get_nowait()
        except queue.Empty:
            if self.block:
                conn = self.pool.get(timeout=timeout)
            else:
                with self.lock:
                    if self.num_connections < self.pool.maxsize:
                        conn = self._new_conn()
                        self.pool.put_nowait(conn)
                        self.num_connections += 1
                        conn = self.pool.get_nowait()
                    else:
                        raise RuntimeError("Pool is exhausted")
        return conn

    def _put_conn(self, conn):
        self.pool.put(conn)

    def _make_request(self, conn, method, url, timeout=..., **httplib_request_kw):
        if timeout is ...:
            timeout = self.timeout
        conn.timeout = timeout
        conn.request(method, url, **httplib_request_kw)
        return self.ResponseCls(conn.getresponse())

    def urlopen(self, method, url, body=None, headers=None, retries=None, redirect=True, assert_same_host=True, timeout=..., pool_timeout=None, **response_kw):
        if retries is None:
            retries = self.retries
        conn = self._get_conn(pool_timeout)
        try:
            response = self._make_request(conn, method, url, timeout=timeout, body=body, headers=headers)
            return response
        except Exception:
            if retries:
                pass
            else:
                raise
        finally:
            self._put_conn(conn)

    def close(self):
        while not self.pool.empty():
            conn = self.pool.get_nowait()
            conn.close()
