import numpy as np
import openai

def get_embedding_from_api(word, model="vicuna-7b-v1.1"):
    if "ada" in model:
        try:
            response = openai.Embedding.create(model=model, input=word)
            embedding = response['data'][0]['embedding']
            return np.array(embedding)
        except Exception as e:
            print(f"OpenAI API error: {e}")
            return None
    else:
        try:
            response = requests.post(
                "http://localhost:5000/embed",
                json={"text": word, "model": model},
                headers={"Content-Type": "application/json"}
            )
            if response.status_code != 200:
                print(f"Local API error: {response.status_code}, {response.text}")
                return None
            data = response.json()
            return np.array(data["embedding"])
        except requests.exceptions.RequestException as e:
            print(f"Local API request error: {e}")
            return None
