import requests
import logging
import mimetypes
import cairosvg

class TelegramClient:
    def __init__(self, chat_id, thread_id, bot_token):
        self.chat_id = chat_id
        self.thread_id = thread_id
        self.bot_token = bot_token

    def send_message(self, message, disable_links_preview=True):
        url = f'https://api.telegram.org/bot{self.bot_token}/sendMessage'
        payload = {
            'chat_id': self.chat_id,
            'text': message,
            'parse_mode': 'Markdown',
            'disable_web_page_preview': str(disable_links_preview).lower()
        }
        if self.thread_id is not None:
            payload['message_thread_id'] = self.thread_id
        response = requests.post(url, json=payload)
        if response.status_code != 200:
            logging.error(f"Failed to send message: {response.text}")

    def send_file(self, file_name, contents):
        if file_name.lower().endswith('.svg'):
            try:
                png_data = cairosvg.svg2png(bytestring=contents)
                new_file_name = file_name.rsplit('.', 1)[0] + '.png'
                file_name = new_file_name
                contents = png_data
                mime_type = 'image/png'
            except Exception as e:
                logging.error(f"SVG conversion failed: {e}")
                return
        else:
            mime_type, _ = mimetypes.guess_type(file_name)
            mime_type = mime_type or 'application/octet-stream'

        if mime_type.startswith('image/'):
            url = f'https://api.telegram.org/bot{self.bot_token}/sendPhoto'
            files = {'photo': (file_name, contents, mime_type)}
        else:
            url = f'https://api.telegram.org/bot{self.bot_token}/sendDocument'
            files = {'document': (file_name, contents, mime_type)}

        data = {'chat_id': self.chat_id}
        if self.thread_id is not None:
            data['message_thread_id'] = self.thread_id

        response = requests.post(url, data=data, files=files)
        if response.status_code != 200:
            logging.error(f"File sending failed: {response.text}")
