import requests
from urllib.parse import quote

class FileOnlineDelete_gitee:
    def __init__(self):
        self.url_prefix = "https://gitee.com/api/v5/repos"
        self.show_help = ""

    def delete_file_from_branch(self, repo_owner: str, repo_name: str, file_path: str, branch: str, access_token: str, commit_message: str = "[comfyui_LLM_party] file deleted") -> tuple:
        try:
            # Step 1: Get the current commit SHA of the branch
            refs_url = f"{self.url_prefix}/{repo_owner}/{repo_name}/git/refs/heads/{quote(branch)}"
            refs_response = requests.get(refs_url, params={'access_token': access_token})
            if refs_response.status_code != 200:
                error_msg = refs_response.json().get('message', 'Error retrieving branch info')
                return (error_msg,)

            commit_sha = refs_response.json()['object']['sha']

            # Step 2: Get the file's SHA
            contents_url = f"{self.url_prefix}/{repo_owner}/{repo_name}/contents/{quote(file_path)}"
            contents_params = {'access_token': access_token, 'ref': branch}
            contents_response = requests.get(contents_url, params=contents_params)
            if contents_response.status_code != 200:
                if contents_response.status_code == 404:
                    return ("File not found",)
                else:
                    error_msg = contents_response.json().get('message', 'Error retrieving file info')
                    return (error_msg,)

            file_sha = contents_response.json()['sha']

            # Step 3: Delete the file
            delete_url = f"{self.url_prefix}/{repo_owner}/{repo_name}/contents/{quote(file_path)}"
            delete_data = {
                'branch': branch,
                'message': commit_message,
                'sha': file_sha
            }
            delete_response = requests.delete(delete_url, params={'access_token': access_token}, data=delete_data)

            if delete_response.status_code == 200:
                return ("Success",)
            else:
                error_msg = delete_response.json().get('message', 'Unknown error occurred')
                return (error_msg,)

        except Exception as e:
            return (str(e),)

