        self.app = Flask(__name__)
        self.api = Api(self.app)
        self.inferencers = {}
        self._load_models(models_dirs or [])
        self._setup_routes()
        self.api.server = self

    def _load_models(self, models_dirs):
        for model_dir in models_dirs:
            if os.path.exists(model_dir):
                model_id = os.path.basename(model_dir)
                class DummyInferencer:
                    def __init__(self, model_id, model_dir):
                        self.metadata = {
                            'id': model_id,
                            'framework': 'dummy',
                            'description': f'Dummy model from {model_dir}'
                        }
                    def predict(self, data):
                        return {'processed': data}
                self.inferencers[model_id] = DummyInferencer(model_id, model_dir)

    def _setup_routes(self):
        self.api.add_resource(ModelListEndpoint, '/models')
        self.api.add_resource(InferenceEndpoint, '/infer/<string:model_id>')

    def run(self, host='127.0.0.1', port=5000):
        self.app.run(host=host, port=port)

class ModelListEndpoint(Resource):
    def get(self):
        server = self.api.server
        models = [{'id': mid, **inf.metadata} for mid, inf in server.inferencers.items()]
        return resp_json(models)

class InferenceEndpoint(Resource):
    def post(self, model_id):
        server = self.api.server
        if model_id not in server.inferencers:
            return resp_json({'error': 'Model not found'}, 404)
        data = request.get_json()
        result = server.inferencers[model_id].predict(data)
        return resp_json({'result': result})
