import pickle
import sqlite3
from flask import Flask, request, render_template
from sklearn.feature_extraction.text import TfidfVectorizer
import numpy as np

class ReviewClassifier:
    def __init__(self):
        self.app = Flask(__name__)
        self.db = 'reviews.db'
        with open('clf.pkl', 'rb') as f:
            self.clf = pickle.load(f)
        with open('vect.pkl', 'rb') as f:
            self.vect = pickle.load(f)
        self.setup_db()
        self.setup_routes()

    def setup_db(self):
        with sqlite3.connect(self.db) as conn:
            conn.execute('''CREATE TABLE IF NOT EXISTS reviews
                         (id INTEGER PRIMARY KEY AUTOINCREMENT,
                          review TEXT NOT NULL,
                          sentiment INTEGER NOT NULL)''')
            conn.commit()

    def setup_routes(self):
        @self.app.route('/', methods=['GET'])
        def index():
            return render_template('index.html')

        @self.app.route('/results', methods=['POST'])
        def results():
            review = request.form['review']
            sentiment, prob = self.classify(review)
            return render_template('results.html', review=review, sentiment=sentiment, probability=prob)

        @self.app.route('/feedback', methods=['POST'])
        def feedback():
            review = request.form['review']
            true_sentiment = int(request.form['true_sentiment'])
            self.train(review, true_sentiment)
            self.sqlite_entry(review, true_sentiment)
            return "Thank you for your feedback!"

    def classify(self, document):
        X = self.vect.transform([document])
        pred = self.clf.predict(X)[0]
        prob = np.max(self.clf.predict_proba(X))
        return ('positive' if pred == 1 else 'negative', prob)

    def train(self, document, y):
        X = self.vect.transform([document])
        self.clf.partial_fit(X, [y], classes=np.array([0, 1]))
        with open('clf.pkl', 'wb') as f:
            pickle.dump(self.clf, f)

    def sqlite_entry(self, document, y):
        with sqlite3.connect(self.db) as conn:
            conn.execute("INSERT INTO reviews (review, sentiment) VALUES (?, ?)", (document, y))
            conn.commit()

    def run(self):
        self.app.run(debug=True)
