from flask import Flask, request, jsonify
from flask_cors import CORS
from langchain.document_loaders import TextLoader
from langchain.text_splitter import CharacterTextSplitter
from langchain.embeddings import HuggingFaceEmbeddings
from langchain.vectorstores import FAISS
from gpt4all import GPT4All
import os
import requests

class FlaskAPI:
    def __init__(self):
        self.app = Flask(__name__)
        CORS(self.app)
        self.embeddings_model_name = "all-MiniLM-L6-v2"
        self.persist_directory = "./persist"
        self.model_type = "gguf"
        self.model_path = "./models/gguf-model"
        self.model_n_ctx = "2048"
        self.llm = None
        self._setup_routes()
        self._load_model()

    def _setup_routes(self):
        @self.app.route('/ingest', methods=['GET'])
        def ingest_data():
            source_dir = request.args.get('source_dir')
            if not source_dir:
                return jsonify({"error": "Missing source_dir parameter"}), 400
            try:
                documents = self._load_documents(source_dir)
                text_splitter = CharacterTextSplitter(chunk_size=1000, chunk_overlap=200)
                texts = text_splitter.split_documents(documents)
                embeddings = HuggingFaceEmbeddings(model_name=self.embeddings_model_name)
                db = FAISS.from_documents(texts, embeddings)
                db.save_local(self.persist_directory)
                return jsonify({"status": "Documents ingested successfully"})
            except Exception as e:
                return jsonify({"error": str(e)}), 500

        @self.app.route('/get_answer', methods=['POST'])
        def get_answer():
            data = request.json
            question = data.get('question')
            if not question:
                return jsonify({"error": "Missing question"}), 400
            try:
                embeddings = HuggingFaceEmbeddings(model_name=self.embeddings_model_name)
                db = FAISS.load_local(self.persist_directory, embeddings)
                docs = db.similarity_search(question, k=3)
                context = " ".join([d.page_content for d in docs])
                prompt = f"Context: {context}\n\nQuestion: {question}\n\nAnswer:"
                response = self.llm.generate(prompt, max_tokens=200)
                return jsonify({"answer": response})
            except Exception as e:
                return jsonify({"error": str(e)}), 500

        @self.app.route('/download_model', methods=['GET'])
        def download_and_save():
            if os.path.exists(self.model_path):
                return jsonify({"status": "Model already exists"})
            try:
                model_url = "https://example.com/model.gguf"  # Replace with actual URL
                os.makedirs(os.path.dirname(self.model_path), exist_ok=True)
                response = requests.get(model_url, stream=True)
                with open(self.model_path, 'wb') as f:
                    for chunk in response.iter_content(chunk_size=8192):
                        f.write(chunk)
                return jsonify({"status": "Model downloaded successfully"})
            except Exception as e:
                return jsonify({"error": str(e)}), 500

    def _load_documents(self, source_dir):
        loader = TextLoader(source_dir, recursive=True)
        return loader.load()

    def run(self):
        self.app.run(host='0.0.0.0', port=5000)

    def _load_model(self):
        if not os.path.exists(self.model_path):
            return
        self.llm = GPT4All(model=self.model_path, n_ctx=int(self.model_n_ctx))
