import threading
from flask import Flask, jsonify, request
from werkzeug.serving import make_server

class MII_Client:
    def __init__(self, deployment_name):
        self.deployment_name = deployment_name

    def process_request(self, data):
        return {"deployment": self.deployment_name, "data": data}

class RestfulGatewayThread(threading.Thread):
    def __init__(self, deployment_name: str, rest_host: str, rest_port: int, rest_procs: int):
        super().__init__()
        self._deployment_name = deployment_name
        self._rest_host = rest_host
        self._rest_port = rest_port
        self._rest_procs = rest_procs
        self._stop_event = threading.Event()
        self.server = None

    def _create_server(self, deployment_name: str, rest_host: str, rest_port: int, rest_procs: int):
        app = Flask(__name__)
        mii_client = MII_Client(deployment_name)
        app.config['mii_client'] = mii_client

        @app.route('/health', methods=['GET'])
        def health():
            return jsonify({"status": "healthy"}), 200

        @app.route('/process', methods=['POST'])
        def process():
            data = request.get_json()
            client = app.config['mii_client']
            result = client.process_request(data)
            return jsonify(result), 200

        @app.route('/terminate', methods=['POST'])
        def terminate():
            server = app.config.get('server')
            if server:
                server.shutdown()
            return jsonify({"status": "shutdown initiated"}), 200

        server = make_server(rest_host, rest_port, app)
        app.config['server'] = server
        return server

    def _shutdown(self):
        if self.server:
            self.server.shutdown()
        self._stop_event.set()

    def run(self):
        self.server = self._create_server(self._deployment_name, self._rest_host, self._rest_port, self._rest_procs)
        self.server.serve_forever()

    def get_stop_event(self):
        return self._stop_event
