import socket
from urllib.parse import urlparse, urlunparse
from http.client import HTTPConnection, HTTPSConnection

class _Default:
    pass

_Default = _Default()

class HTTPConnectionPool:
    def __init__(self, scheme='http', host=None, port=None, timeout=None, retries=None, proxy=None, proxy_headers=None, conn_kw=None):
        self.scheme = scheme
        self.host = host
        self.port = port
        self.timeout = timeout
        self.retries = retries
        self.proxy = proxy
        self.proxy_headers = proxy_headers or {}
        self.conn_kw = conn_kw or {}
        if scheme.lower() == 'https':
            self.ConnectionCls = HTTPSConnection
        else:
            self.ConnectionCls = HTTPConnection
        self._pool = []
        self.num_connections = 0

    def _new_conn(self):
        if self.proxy:
            parsed_proxy = urlparse(self.proxy)
            if parsed_proxy.scheme == 'https':
                conn_class = HTTPSConnection
                default_port = 443
            else:
                conn_class = HTTPConnection
                default_port = 80
            proxy_host = parsed_proxy.hostname
            proxy_port = parsed_proxy.port or default_port
            conn = conn_class(proxy_host, proxy_port, **self.conn_kw)
        else:
            conn = self.ConnectionCls(self.host, self.port, **self.conn_kw)
        self.num_connections += 1
        return conn

    def _make_request(self, conn, method, url, timeout=_Default, chunked=False, **httplib_request_kw):
        if timeout is _Default:
            timeout_val = self.timeout
        else:
            timeout_val = timeout
        conn.timeout = timeout_val
        headers = httplib_request_kw.get('headers', {})
        if chunked:
            headers['Transfer-Encoding'] = 'chunked'
        if self.proxy:
            headers.update(self.proxy_headers)
        if 'headers' in httplib_request_kw:
            httplib_request_kw['headers'] = headers
        else:
            httplib_request_kw['headers'] = headers
        try:
            conn.request(method, url, **httplib_request_kw)
            response = conn.getresponse()
            return response
        except socket.timeout:
            raise
        except Exception as e:
            raise

    def urlopen(self, method, url, body=None, headers=None, retries=None, redirect=True, assert_same_host=True, timeout=_Default, pool_timeout=None, release_conn=None, chunked=False, **response_kw):
        if self.proxy:
            target_url = urlunparse((self.scheme, f"{self.host}:{self.port}" if self.port else self.host, url, '', '', ''))
        else:
            target_url = url
        conn = self._get_connection(pool_timeout)
        if headers is None:
            headers = {}
        if self.proxy:
            headers['Host'] = self.host
        httplib_request_kw = {
            'body': body,
            'headers': headers,
        }
        current_retries = retries if retries is not None else self.retries
        for attempt in range(current_retries + 1):
            try:
                response = self._make_request(conn, method, target_url, timeout, chunked, **httplib_request_kw)
                if redirect and response.status in (301, 302, 303, 307, 308):
                    location = response.getheader('Location')
                    if location:
                        return self.urlopen(method, location, body, headers, retries, redirect, assert_same_host, timeout, pool_timeout, release_conn, chunked, **response_kw)
                if release_conn is None:
                    release_conn = not response.should_close
                return response
            except (socket.timeout, socket.error) as e:
                if attempt < current_retries:
                    self._put_connection(conn, False)
                    conn = self._get_connection(pool_timeout)
                else:
                    raise
        raise Exception("Max retries exceeded")

    def _get_connection(self, pool_timeout=None):
        if self._pool:
            return self._pool.pop()
        else:
            return self._new_conn()

    def _put_connection(self, conn, release=True):
        if release:
            self._pool.append(conn)
        else:
            conn.close()

