def process_circleci_event(headers, msg):
    supported_types = ["workflow_job_completed", "workflow_completed", "check_run_completed"]
    event_type = headers.get("Circleci-Event-Type")
    if event_type not in supported_types:
        raise ValueError(f"Unsupported event type: {event_type}")

    data_str = base64.b64decode(msg["data"]).decode("utf-8")
    payload = json.loads(data_str)

    return {
        "event_type": event_type,
        "id": payload.get("id"),
        "metadata": json.dumps(payload),
        "time_created": payload.get("created_at"),
        "signature": headers["Circleci-Signature"],
        "msg_id": msg["message_id"],
        "source": "circleci"
    }

@app.route("/", methods=["POST"])
def index():
    try:
        envelope = request.get_json()
        if not envelope or "message" not in envelope:
            abort(400)

        msg = envelope["message"]
        if "attributes" not in msg or "data" not in msg:
            abort(400)

        attributes = msg["attributes"]
        if "headers" not in attributes:
            abort(400)

        try:
            headers = json.loads(attributes["headers"])
        except json.JSONDecodeError:
            abort(400)

        if "Circleci-Event-Type" not in headers or "Circleci-Signature" not in headers:
            abort(400)

        event_data = process_circleci_event(headers, msg)

        table_id = "your-project.your_dataset.circleci_events"
        errors = client.insert_rows_json(table_id, [event_data])
        if errors:
            app.logger.warning(f"BigQuery insert errors: {errors}")

        return "", 204
    except Exception as e:
        app.logger.warning(f"Processing error: {e}", exc_info=True)
        return "", 204

