import flask
from datetime import datetime
from typing import Dict, Any, Set, Optional

class FlaskAPI:
    def __init__(self, config):
        self.app = flask.Flask(__name__)
        self.config = config
        self.next_user_id = 1
        self.last_modified = datetime.now().isoformat()
        self.users: Dict[int, Dict[str, Any]] = {}
        self.deleted_orders: Set[str] = set()
        self._setup_routes()

    def _setup_routes(self):
        self.app.route('/spec', methods=['GET'])(self.get_spec)
        self.app.route('/users/<int:user_id>', methods=['GET'])(self.get_user)
        self.app.route('/users', methods=['GET'])(self.list_users)
        self.app.route('/users', methods=['POST'])(self.create_user)
        self.app.route('/users/<int:user_id>', methods=['PUT'])(self.update_user)
        self.app.route('/users/<int:user_id>', methods=['DELETE'])(self.delete_user)
        self.app.route('/orders/<order_id>', methods=['DELETE'])(self.delete_order)
        self.app.before_request(self.check_auth)

    def get_spec(self):
        return flask.jsonify({
            "openapi": "3.0.0",
            "info": {
                "title": "User Management API",
                "version": "1.0.0"
            },
            "paths": {
                "/users": {
                    "get": {"summary": "List users"},
                    "post": {"summary": "Create user"}
                },
                "/users/{user_id}": {
                    "get": {"summary": "Get user"},
                    "put": {"summary": "Update user"},
                    "delete": {"summary": "Delete user"}
                },
                "/orders/{order_id}": {
                    "delete": {"summary": "Delete order"}
                }
            }
        })

    def get_user(self, user_id: int):
        if self.config.simulate_get_user_error:
            return flask.jsonify({"error": "Simulated error"}), 500
        user = self.users.get(user_id)
        if not user:
            return flask.jsonify({"error": "User not found"}), 404
        return flask.jsonify(user)

    def list_users(self):
        return flask.jsonify(list(self.users.values()))

    def create_user(self):
        data = flask.request.get_json()
        if not data:
            return flask.jsonify({"error": "No data provided"}), 400
        if self.config.block_user_creation:
            return flask.jsonify({"error": "Creation blocked"}), 403
        user_id = self.next_user_id
        self.users[user_id] = {
            'id': user_id,
            'name': data.get('name'),
            'email': data.get('email')
        }
        self.next_user_id += 1
        self.last_modified = datetime.now().isoformat()
        return flask.jsonify(self.users[user_id]), 201

    def update_user(self, user_id: int):
        if user_id not in self.users:
            return flask.jsonify({"error": "User not found"}), 404
        data = flask.request.get_json()
        if not data:
            return flask.jsonify({"error": "No data provided"}), 400
        if self.config.fail_on_update:
            return flask.jsonify({"error": "Simulated update failure"}), 500
        user = self.users[user_id]
        user.update(data)
        self.last_modified = datetime.now().isoformat()
        return flask.jsonify(user)

    def delete_user(self, user_id: int):
        if user_id not in self.users:
            return flask.jsonify({"error": "User not found"}), 404
        if self.config.prevent_deletion:
            return flask.jsonify({"error": "Deletion not allowed"}), 403
        del self.users[user_id]
        self.last_modified = datetime.now().isoformat()
        return flask.jsonify({"message": "User deleted"}), 200

    def delete_order(self, order_id: str):
        if self.config.order_deletion_fails:
            return flask.jsonify({"error": "Order deletion failed"}), 500
        self.deleted_orders.add(order_id)
        return flask.jsonify({"message": "Order deleted"}), 200

    def check_auth(self):
        if self.config.auth_required and not flask.request.headers.get('Authorization'):
            return flask.jsonify({"error": "Authentication required"}), 401

