            return redirect(url_for('login'))

        @self.app.route('/login', methods=['GET', 'POST'])
        def login():
            form = self.LoginForm()
            if form.validate_on_submit():
                user = self.User.query.filter_by(username=form.username.data).first()
                if user and user.check_password(form.password.data):
                    login_user(user)
                    return redirect(url_for('index'))
            return render_template('login.html', form=form)

        @self.app.route('/signup', methods=['GET', 'POST'])
        def signup():
            form = self.RegistrationForm()
            if form.validate_on_submit():
                if self.User.query.filter_by(username=form.username.data).first():
                    return "Username already exists"
                if self.User.query.filter_by(email=form.email.data).first():
                    return "Email already exists"
                user = self.User(username=form.username.data, email=form.email.data)
