            if form.validate_on_submit():
                if current_user.check_password(form.old_password.data):
                    current_user.set_password(form.new_password.data)
                    self.db.session.commit()
                    return redirect(url_for('index'))
                else:
                    return "Invalid password"
            return render_template('settings.html', form=form)

        @self.app.route('/logout')
        @self.login_required
        def logout():
            logout_user()
            return redirect(url_for('login'))

        @self.app.route('/')
        @self.login_required(role='ANY')
        def index():
            return f"Welcome {current_user.username} ({current_user.get_role()})"

        @self.app.route('/case', methods=['GET', 'POST'])
        @self.login_required(role='admin')
        def case():
            form = self.CaseForm()
            if form.validate_on_submit():
                return f"Case submitted: {form.case_data.data}"
            return render_template('case.html', form=form)

        self.db.create_all()

    def run(self, host='0.0.0.0', port=8080):
        self.app.run(host=host, port=port)

    def login_required(self, role="ANY"):
        def decorator(func):
            def wrapped(*args, **kwargs):
                if not current_user.is_authenticated:
                    return redirect(url_for('login'))
                if role != "ANY" and current_user.get_role() != role:
                    return redirect(url_for('unauthorized'))
                return func(*args, **kwargs)
            return wrapped
        return decorator

