class HTTPConnectionPool:
    def __init__(self, host, port=None, timeout=None, maxsize=1, block=False, headers=None, retries=None, _proxy=None, _proxy_headers=None, **conn_kw):
        self.scheme = conn_kw.pop('scheme', 'http')
        self.host = host
        self.port = port
        self.timeout = timeout or http.client._Default
        self.retries = retries
        self.proxy = _proxy
        self.proxy_headers = _proxy_headers or {}
        self.conn_kw = conn_kw
        self.pool = queue.LifoQueue(maxsize=maxsize)
        self.block = block

    def _new_conn(self):
        if self.scheme == 'https':
            conn_class = http.client.HTTPSConnection
        else:
            conn_class = http.client.HTTPConnection
        if self.proxy:
            parsed_proxy = urlparse(self.proxy)
            proxy_host = parsed_proxy.hostname
            proxy_port = parsed_proxy.port or 80
            conn = conn_class(proxy_host, proxy_port, timeout=self.timeout, **self.conn_kw)
        else:
            if self.port:
                port = self.port
            else:
                port = 443 if self.scheme == 'https' else 80
            conn = conn_class(self.host, port, timeout=self.timeout, **self.conn_kw)
        return conn

    def _make_request(self, conn, method, url, timeout=http.client._Default, chunked=False, **httplib_request_kw):
        headers = httplib_request_kw.get('headers', {})
        if self.proxy:
            headers.update(self.proxy_headers)
        try:
            if timeout is not http.client._Default:
                conn.sock.settimeout(timeout)
        except Exception:
            pass
        conn.request(method, url, **httplib_request_kw)
        return conn.getresponse()

    def urlopen(self, method, url, body=None, headers=None, retries=None, redirect=True, assert_same_host=True, timeout=http.client._Default, pool_timeout=None, release_conn=None, chunked=False, **response_kw):
        num_retries = retries if retries is not None else (self.retries if hasattr(self, 'retries') else 3)
        while num_retries >= 0:
            try:
                conn = self._get_connection(pool_timeout)
                if self.proxy:
                    host_with_port = f"{self.host}:{self.port}" if self.port not in (80, 443) else self.host
                    full_url = f"{self.scheme}://{host_with_port}{url}"
                    request_url = full_url
                else:
                    request_url = url
                response = self._make_request(
                    conn, method, request_url, timeout=timeout, chunked=chunked, headers=headers, body=body
                )
                return response
            except (socket.timeout, http.client.HTTPException, ConnectionResetError) as e:
                num_retries -= 1
                if num_retries < 0:
                    raise
                if conn:
                    conn.close()
        raise Exception("Request failed after retries")

    def _get_connection(self, pool_timeout):
        try:
            if self.block:
                conn = self.pool.get(timeout=pool_timeout)
            else:
                conn = self.pool.get_nowait()
        except (queue.Empty, queue.Full):
            conn = self._new_conn()
        return conn

    def _put_connection(self, conn):
        try:
            self.pool.put_nowait(conn)
        except queue.Full:
            pass
