    def make_response(self, rv):
        if isinstance(rv, tuple):
            length = len(rv)
            if length not in (2, 3):
                return super().make_response(rv)
            if length == 3:
                response_data, status, headers = rv
            else:
                response_data, status = rv
                headers = None
            if isinstance(response_data, self.json_data_class):
                json_data = json.dumps(response_data)
                resp = Response(json_data, content_type='application/json')
                resp.status_code = status
                if headers:
                    resp.headers.extend(headers)
                return resp
            else:
                return super().make_response(rv)
        else:
            if isinstance(rv, self.json_data_class):
                return Response(json.dumps(rv), content_type='application/json')
            else:
                return super().make_response(rv)

