        self.server_thread = None
        self._setup_routes()

    def _setup_routes(self):
        @self.app.route('/status', methods=['GET'])
        def status():
            return jsonify({"status": "Server is running"})

        @self.app.route('/input', methods=['POST'])
        def input_handler():
            data = request.json
            text = data.get('text', '')
            screen = data.get('screen', '')
            talk = data.get('talk', '')
            return jsonify(self._the_input(text, screen, talk))

    def _the_input(self, text: str, screen: str, talk: str) -> dict:
        return {
            "processed_text": text.upper(),
            "screen": screen,
            "talk": talk
        }

    def start_api(self, api: bool = False) -> None:
        if api:
            self.server_thread = threading.Thread(target=self._start_waitress)
            self.server_thread.start()
        else:
            self.server_thread = self.ServerThread(self.app, '0.0.0.0', 5000)
            self.server_thread.start()

    def _start_waitress(self):
        waitress.serve(self.app, host='0.0.0.0', port=5000)

    def stop_api(self) -> None:
        if self.server_thread and isinstance(self.server_thread, self.ServerThread):
            self.server_thread.shutdown()
            self.server_thread.join()

    class ServerThread(threading.Thread):
        def __init__(self, app, host, port):
            super().__init__()
            self.srv = WSGIServer((host, port), app)
            self.ctx = app.app_context()
            self.ctx.push()

        def run(self):
            self.srv.serve_forever()

        def shutdown(self):
            self.srv.shutdown()

