        'auth_code': auth_code
    }
    return json.dumps({
        'qr_url': f'https://qrcode.example.com?sid={sid}'
    })

@app.route('/check_qrcode/<sid>', methods=['GET'])
def check_qrcode(sid):
    session = sessions.get(sid)
    if not session:
        return json.dumps({'error': 'session_not_found'})
    return json.dumps({'status': session['status']})

@app.route('/get_tokens', methods=['POST'])
def get_tokens():
    data = flask.request.get_json()
    auth_code = data.get('auth_code')

    for sid, info in sessions.items():
        if info.get('auth_code') == auth_code:
            info['status'] = 'processed'
            return json.dumps({
                'status': 'success',
                'tokens': {
                    'access_token': secrets.token_urlsafe(16),
                    'refresh_token': secrets.token_urlsafe(16)
                }
            })

    return json.dumps({'error': 'invalid_auth_code'}), 400

if __name__ == '__main__':
    app.run(debug=True)

