        if not data or 'coordinates' not in data:
            return jsonify({"error": "Missing coordinates in request body"}), 400

        try:
            result = self.model.predict(data['coordinates'])
            return jsonify({"potential": result}), 200
        except Exception as e:
            return jsonify({"error": str(e)}), 500

    def gradient(self):
        data = request.get_json()
        if not data or 'coordinates' not in data:
            return jsonify({"error": "Missing coordinates in request body"}), 400

        try:
            gradient = self.model.compute_gradient(data['coordinates'])
            return jsonify({"gradient": gradient.tolist()}), 200
        except Exception as e:
            return jsonify({"error": str(e)}), 500

    def minimize(self):
        data = request.get_json()
        if not data or 'initial_structure' not in data:
            return jsonify({"error": "Missing initial_structure in request body"}), 400

        try:
            optimized_structure, final_energy = self.model.minimize(data['initial_structure'])
            return jsonify({
                "optimized_structure": optimized_structure.tolist(),
                "final_energy": float(final_energy)
            }), 200
        except Exception as e:
            return jsonify({"error": str(e)}), 500

