from flask import Flask, Response, request
import re
import os
import base64
import json
import librato
import statsd
from blueprint import Blueprint
import backend

class MeteredResponse(Response):
    def __init__(self, *args, **kwargs):
        super(MeteredResponse, self).__init__(*args, **kwargs)
        content_length = len(kwargs.get('response', ''))
        if 0 < content_length:
            librato.count('blueprint-io-server.bandwidth.out', content_length)
            statsd.update('blueprint-io-server.bandwidth.out', content_length)

class BlueprintServer:
    def __init__(self):
        self.app = Flask(__name__)
        self._setup_routes()
        self.browser_pattern = re.compile(r'Chrome|Gecko|Microsoft|Mozilla|Safari|WebKit')

    def _setup_routes(self):
        self.app.route('/secret', methods=['GET'])(self.secret)
        self.app.route('/<secret>/<name>', methods=['GET'])(self.get_blueprint)
        self.app.route('/<secret>/<name>', methods=['PUT'])(self.put_blueprint)
        self.app.route('/<secret>/<name>/<sha>.tar', methods=['GET'])(self.get_tarball)
        self.app.route('/<secret>/<name>/<sha>.tar', methods=['PUT'])(self.put_tarball)
        self.app.route('/<secret>/<name>/<name2>.sh', methods=['GET'])(self.sh)
        self.app.route('/<secret>/<name>/user-data.sh', methods=['GET'])(self.user_data)

    def _blueprint(self, secret, name):
        data = backend.get_blueprint(secret, name)
        if data is None:
            return None
        elif data is False:
            return False
        b = Blueprint()
        b.name = name
        b.update(json.loads(data))
        return b

    def _server(self):
        return request.url_root

    def _validate_secret(self, secret):
        if re.match(r'^[0-9A-Za-z_-]{64}$', secret) is None:
            abort(400)

    def _validate_name(self, name):
        if re.search(r'[/ \t\r\n]', name) is not None:
            abort(400)

    def _validate_sha(self, sha):
        if re.match(r'^[0-9a-f]{40}$', sha) is None:
            abort(400)

    def secret(self):
        while True:
            s = base64.urlsafe_b64encode(os.urandom(48))
            try:
                iter(backend.list(s)).next()
            except StopIteration:
                break
        return MeteredResponse(response='{0}\n'.format(s),
                             status=201,
                             content_type='text/plain')

    def get_blueprint(self, secret, name):
        self._validate_secret(secret)
        self._validate_name(name)

        content_length = backend.head_blueprint(secret, name)
        if content_length is None:
            abort(404)

        if self.browser_pattern.search(request.environ.get('HTTP_USER_AGENT', '')) or 'html' == request.args.get('format'):
            librato.count('blueprint-io-server.renders')
            statsd.increment('blueprint-io-server.renders')
