from flask import Flask, request, jsonify
import json
import os

app = Flask(__name__)
COLORS_FILE = 'colors.json'

def load_colors():
    if not os.path.exists(COLORS_FILE):
        with open(COLORS_FILE, 'w') as f:
            json.dump({}, f)
    with open(COLORS_FILE, 'r') as f:
        return json.load(f)

def save_colors(colors):
    with open(COLORS_FILE, 'w') as f:
        json.dump(colors, f)

@app.route('/colors', methods=['GET'])
def get_all_colors():
    colors = load_colors()
    return jsonify(colors)

@app.route('/colors/<name>', methods=['GET'])
def get_color(name):
    colors = load_colors()
    if name in colors:
        return jsonify({name: colors[name]})
    return jsonify({'error': 'Color not found'}), 404

@app.route('/colors', methods=['POST'])
def create_color():
    data = request.get_json()
    name = data.get('name')
    value = data.get('value')
    if not name or not value:
        return jsonify({'error': 'Name and value are required'}), 400
    colors = load_colors()
    if name in colors:
        return jsonify({'error': 'Color already exists'}), 400
    colors[name] = value
    save_colors(colors)
    return jsonify({name: value}), 201

@app.route('/colors/<name>', methods=['PUT'])
def update_color(name):
    data = request.get_json()
    value = data.get('value')
    colors = load_colors()
    if name not in colors:
        return jsonify({'error': 'Color not found'}), 404
    if value is not None:
        colors[name] = value
        save_colors(colors)
    return jsonify({name: colors[name]})

@app.route('/colors/<name>', methods=['DELETE'])
def delete_color(name):
    colors = load_colors()
    if name not in colors:
        return jsonify({'error': 'Color not found'}), 404
    value = colors[name]
    del colors[name]
    save_colors(colors)
    return jsonify({name: value})

if __name__ == '__main__':
    app.run(debug=True)

