                self._conn.send(data)
                self._conn.send(b"\r\n")
                break
            except (ConnectionError, OSError) as e:
                self._reconnect()
                self._tries += 1
                time.sleep(self._delay)
                self._delay *= 2
        else:
            raise Exception("Max retries exceeded")

    def _reconnect(self):
        if self._conn:
            self._conn.close()
        self._conn = None
        self._connect()
        self._tries = 0
        self._delay = 1

