from flask import Flask, request, jsonify
import json

app = Flask(__name__)

# Agent's state
agent_inventory = {}
agent_queue = []
balance = 0.0

@app.route('/')
def home():
    return jsonify({'status': 'OK'})

@app.route('/inventory')
def inventory():
    return jsonify(agent_inventory)

@app.route('/queue')
def queue():
    return jsonify(agent_queue)

@app.route('/balance')
def balance_check():
    return jsonify(balance)

@app.route('/trade')
def trade():
    data_str = request.args.get('data')
    if not data_str:
        return jsonify({'status': 'error', 'message': 'Missing data parameter'})
    try:
        data = json.loads(data_str)
    except json.JSONDecodeError:
        return jsonify({'status': 'error', 'message': 'Invalid JSON format'})
    action = data.get('action')
    item = data.get('item')
    quantity = data.get('quantity')
    price = data.get('price')
    if not all([action, item, quantity, price]):
        return jsonify({'status': 'error', 'message': 'Missing required fields'})
    try:
        quantity = int(quantity)
        price = float(price)
    except (ValueError, TypeError):
        return jsonify({'status': 'error', 'message': 'Invalid quantity or price'})
    action = action.lower()
    if action not in ['buy', 'sell']:
        return jsonify({'status': 'error', 'message': 'Invalid action'})
    total_cost = quantity * price
    if action == 'buy':
        if balance >= total_cost:
            balance -= total_cost
            if item in agent_inventory:
                agent_inventory[item] += quantity
            else:
                agent_inventory[item] = quantity
            agent_queue.append({
                'action': action,
                'item': item,
                'quantity': quantity,
                'price': price
            })
            return jsonify({
                'status': 'success',
                'message': f'Bought {quantity} units of {item}'
            })
        else:
            return jsonify({
                'status': 'error',
                'message': 'Insufficient balance'
            })
    else:  # sell
        current_quantity = agent_inventory.get(item, 0)
        if current_quantity >= quantity:
            agent_inventory[item] = current_quantity - quantity
            if agent_inventory[item] == 0:
                del agent_inventory[item]
            balance += total_cost
            agent_queue.append({
                'action': action,
                'item': item,
                'quantity': quantity,
                'price': price
            })
            return jsonify({
                'status': 'success',
                'message': f'Sold {quantity} units of {item}'
            })
        else:
            return jsonify({
                'status': 'error',
                'message': 'Insufficient inventory'
            })

@app.route('/reset')
def reset():
    money_str = request.args.get('money')
    if not money_str:
        return jsonify(False)
    try:
        new_money = float(money_str)
    except ValueError:
        return jsonify(False)
    if new_money < 0:
        return jsonify(False)
    global balance
    balance = new_money
    return jsonify(True)

if __name__ == '__main__':
