import flask
import threading
import time
import datetime
import csv
import json
import uuid
from dnstwist import DomainScan

class DNSTwistAPI:
    def __init__(self):
        self.app = flask.Flask(__name__)
        self.sessions = []
        self._setup_routes()
        self._start_janitor()

    def _setup_routes(self):
        @self.app.route('/')
        def root():
            return self.root()

        @self.app.route('/api/scan', methods=['POST'])
        def api_scan():
            return self.api_scan()

        @self.app.route('/api/status/<sid>')
        def api_status(sid):
            return self.api_status(sid)

        @self.app.route('/api/domains/<sid>')
        def api_domains(sid):
            return self.api_domains(sid)

        @self.app.route('/api/json/<sid>')
        def api_json(sid):
            return self.api_json(sid)

        @self.app.route('/api/csv/<sid>')
        def api_csv(sid):
            return self.api_csv(sid)

        @self.app.route('/api/list/<sid>')
        def api_list(sid):
            return self.api_list(sid)

        @self.app.route('/api/stop/<sid>', methods=['POST'])
        def api_stop(sid):
            return self.api_stop(sid)

    def _start_janitor(self):
        janitor_thread = threading.Thread(target=self.janitor, daemon=True)
        janitor_thread.start()

    def janitor(self):
        while True:
            now = datetime.datetime.now()
            for session in list(self.sessions):
                if not session.running:
                    if (now - session.completed_at).total_seconds() > 300:
                        self.sessions.remove(session)
                elif (now - session.start_time).total_seconds() > 3600:
                    session.stop()
            time.sleep(60)

    def root(self):
        return "DNSTwist API - Use /api/scan to start a domain scan"

    def api_scan(self):
        data = flask.request.get_json()
        domain = data.get('domain')
        if not domain:
            return flask.jsonify({'error': 'Missing domain parameter'}), 400

        sid = str(uuid.uuid4())
        scanner = DomainScan()
        scanner.generate_permutations(domain)
        session = {
            'sid': sid,
            'status': 'running',
            'start_time': datetime.datetime.now(),
            'results': [],
            'scanner': scanner,
            'running': True,
            'completed_at': None
        }

        def run_scan():
            try:
                scanner.scan(scanner.permutations, callback=session['results'].append)
            finally:
                session['running'] = False
                session['completed_at'] = datetime.datetime.now()

        threading.Thread(target=run_scan).start()
        self.sessions.append(session)
        return flask.jsonify({'sid': sid}), 201

    def api_status(self, sid):
        session = next((s for s in self.sessions if s['sid'] == sid), None)
        if not session:
            return flask.abort(404)
        return flask.jsonify({
            'status': session['status'],
            'started': session['start_time'].isoformat(),
            'running': session['running']
        })

    def api_domains(self, sid):
        session = next((s for s in self.sessions if s['sid'] == sid), None)
        if not session:
            return flask.abort(404)
        return flask.jsonify([{
            'domain': r['domain'],
            'reason': r['reason']
        } for r in session['results']])

    def api_json(self, sid):
        session = next((s for s in self.sessions if s['sid'] == sid), None)
        if not session:
            return flask.abort(404)
        return flask.jsonify(session['results'])

    def api_csv(self, sid):
        session = next((s for s in self.sessions if s['sid'] == sid), None)
        if not session:
            return flask.abort(404)
        output = []
        writer = csv.writer(output)
        writer.writerow(['domain', 'reason'])
        for r in session['results']:
            writer.writerow([r['domain'], r['reason']])
        return (''.join(output), 200, {'Content-Type': 'text/csv'})

    def api_list(self, sid):
        session = next((s for s in self.sessions if s['sid'] == sid), None)
        if not session:
            return flask.abort(404)
        return ('\n'.join(r['domain'] for r in session['results']) + '\n', 200, {'Content-Type': 'text/plain'})

    def api_stop(self, sid):
        session = next((s for s in self.sessions if s['sid'] == sid), None)
        if not session:
            return flask.abort(404)
        if session['running']:
            session['scanner'].stop()
            session['running'] = False
            session['completed_at'] = datetime.datetime.now()
        return flask.jsonify({'status': 'stopped'})

    def run(self, host='0.0.0.0', port=5000):
        self.app.run(host=host, port=port)

