        logger.error("Host is required")
        raise ValueError("Host is required")
    port = params.get('port')
    scheme = params.get('scheme', 'http').lower()
    proxy = params.get('proxy')

    if port is None:
        if scheme == 'http':
            port = 80
        elif scheme == 'https':
            port = 443
        else:
            logger.error("Invalid scheme: %s", scheme)
            raise ValueError("Invalid scheme")

    use_proxy = bool(proxy)
    if use_proxy:
        proxy_host = proxy.get('host')
        proxy_port = proxy.get('port', 80)
        if not proxy_host:
            logger.error("Proxy host is required")
            raise ValueError("Proxy host is required")
        logger.debug("Using proxy: %s:%s", proxy_host, proxy_port)
        if method == 'GET' and param:
            query = urlencode(param)
            full_path = "{}?{}".format(action, query)
        else:
            full_path = action
        full_url = "{}://{}:{}{}".format(scheme, host, port, full_path)
        conn = http.client.HTTPConnection(proxy_host, proxy_port)
        logger.debug("Connecting to proxy: %s:%s", proxy_host, proxy_port)
    else:
        if scheme == 'http':
            conn = http.client.HTTPConnection(host, port)
        else:
            conn = http.client.HTTPSConnection(host, port)
        logger.debug("Connecting to %s://%s:%s", scheme, host, port)
        if method == 'GET' and param:
            query = urlencode(param)
            path = "{}?{}".format(action, query)
        else:
